@-moz-document domain("proxer.me") {
/*== Allgemein ==*/
body {
  background-color: rgba(35,35,35,1)   !important;
  background: url(/*[[wallpaper]]*/) fixed;
  background-size: 100% auto; }


/*== Bild-Mitte ==*/
#main {
  background-color: rgba(65,65,65,0.7) !important;
  border:           1px solid black    !important; }


/*== Navigations Balken ==*/
#nav {
  background-color: rgba(55,55,55,0.8) !important;
  border-bottom:    2px solid black    !important;
  color:            black              !important; }


div.inner { border: 1px solid black    !important; }


/*== DropDownMenu ==*/
.topmenu ul {
  background-color: rgba(55,55,55,0.9) !important;
  border-color:     black              !important;
  border-right:     1px solid black    !important;
  box-shadow:     5px 5px 25px rgba(10,10,10,0.8); }


/*== DropDownAcc ==*/
#notificationBubble, #loginBubble, .customBubble {
  background: rgba(55,55,55,0.9);
  border:     1px solid black          !important;
  box-shadow: 5px 5px 25px rgba(10,10,10,0.8);
  color:      white; }


#notificationBubble:after, #loginBubble:after, .customBubble:after {
  border-color:            rgba(55,55,55,0.75) !important;
  border-right: 15px solid rgba(0,0,0,0.0)     !important;
  border-left:  15px solid rgba(0,0,0,0.0)     !important; }


/*== MainBar ==*/
ul#simple-navi, ul#simple-navi-wiki, div.inner {
  background-color: rgba(55,55,55,0.6)    !important;
  border-bottom:    1px solid black; }



/*=============== Test =====================================================================*/

ul#simple-navi-wiki  {
  border:           0px 0px               !important;
  background-color: rgba(45,45,45,0.4)    !important; }

div#rightColumn {
  background-color: rgba(55,55,55,0.4)    !important; }

div#content {
  background-color: rgba(255,255,255,0.6) !important; }

* { border-radius:  0px                   !important; }

/*== Profil-bearbeiten Background fix ==*/
#tinymce {background: rgba(45,45,45,0.4)  !important;}

/*==========================================================================================*/



/*== removing Header ==*/
/*[[Header]]*/


/*== Cookie-Nachfrage Fenster Mod ==*/
/*[[Cookie]]*/


/*== Chat ==*/
div#chat {
  background-color: rgba(55,55,55,0.75) !important;
  border:           1px solid black;
  border-radius:    0px; }

/*== PopUp Transparenz (Ladesymbol, usw.) ==*/
div.message, div#loading {background-color: rgba(55,55,55,0.75) !important;}

