@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {
  body {background: url(http://ya-vidoc3000.narod.ru/fon/1.jpg) fixed; -webkit-background-size: cover; background-size: cover;}
/*Заголовок*/
  #main-header {background: none; padding-top: 20px; color: red !important;}
  #Head {background: none !important; color: black !important;}
  #Head a {color: black !important;}
  #Head a:hover {color: #00FF00 !important;}
  .SiteTitle {background-color: white !important;}
  /*Контент сайта*/
  #main-article {background: none !important;}
  /*Убираем лишнее*/
  .ad, #social {display: none !important;}
  /*Меню сайта*/
  #left-sidebar > * {background-color: white !important; opacity: 0.7; position: fixed !important;}
  #left-sidebar > *:hover {opacity: 1;}
  /*Таблица со стилями пользователя*/
  .author-styles {background-color: white !important; opacity:0.7;}
  ul, dl, h2, p, h3 {background-color: white !important; opacity: 0.7; text-shadow: 1px 1px 2px black, 0 0 1em red; font-size:15px;}
  #Panel {padding-top: 20px;}
  .style-brief-text {background-color: white !important; opacity: 0.7;}
  #additional-info-text, #info-tables {background-color: white !important; opacity: 0.7; max-height: 50em;}
  #Content { opacity: 0.7;}
  footer {float: right !important;}
  footer a:hover {background-color: red;}
  .style-brief-text a {color: red;}
  a:hover {color: #00FF00;}
  .Discussion:hover, .ItemContent:hover, .DataList:hover {opacity: 1;}
  #Content {font-size: 15px;}
  #Content:hover {opacity: 1;}
  .Item.Read {background-color: black !important;}
  /*какой-то мусор*/
  #related {display: none !important;}
  .PageControls {background-color: white; opacity: 0.7;}
  .HomepageTitle {color: white !important;}
  #long-description, #control-panel {background-color: white !important; opacity: 0.7 !important;}
}
