css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.aywas.com/avt/") {
  
  #avt-dialogue {height: 400px}
  #dialogue-text {height: 200px; position:relative; top:100px;}
  #dialogue-text > img {position:absolute; top:-120px;}
  #dialogue-actions {height: 100px; position:relative; top: -200px }
  
  
  
 input[name='explore'] { position: absolute;
    right: 0px;
   z-index: 1;
}
  input[name='jump'] {position: absolute;}
  input[name='accept'] {position: absolute;}
  input[name='help'] {position: absolute;}
  input[name='greet'] {position: absolute;}
  input[name='key'] {position: absolute;}
  input[name='fight'] {position: absolute;
   width:120px;}
  input[name='pick'] { position: absolute;
    right: 0px;
    width: 200px;
    z-index: 2;
}
    input[name='collect'] { position: absolute;
    right: 0px;
      width: 200px;
    z-index: 2;
}
  

}
