css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reboot.ms") {
  /*parte superiore header*/
  #userBar .navTabs { background: /*[[colora]]*/ url('http://www.reboot.ms/forum/styles/arduinoux/arduinoux/circuit_bg.png') no-repeat 0% 50%; }
  
  /*pulsante di registrazione*/
  #SignupButton .inner { background: url("http://www.reboot.ms/forum/styles/arduinoux/arduinoux/speck.png") repeat scroll 0px 0px /*[[colora]]*/}

  /*parte inferiore header e forum*/
  .heading, .xenForm .formHeader, .navTabs, .uix_collapseNodes .uix_icon, .node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon, .PageNav a.currentPage, .userBanner.bannerStaff, .button.primary, .message .newIndicator, .Popup .PopupControl.PopupOpen,	.Popup.PopupContainerControl.PopupOpen, a.callToAction span, #userBar .navTabs .navTab.Popup.PopupContainerControl.PopupOpen, #userBar .navTabs .navTab.selected.PopupOpen .navLink, .message .alternativeBestAnswerIndicator span, .Tinhte_XenTag_TagCloud .Tinhte_XenTag_TagCloud_Level3, .message .messageMeta .control.PopupOpen, .downloadButton .inner, .uix_sidePane ul .selected .navLink, .EWRporta2_ArticleView .messageSimple .newIndicator { background-color: /*[[colorb]]*/; }

  /*forum*/
  .nodeList .categoryStrip { background-color: /*[[colorb]]*/ !important; }

  /*news*/
  .bbtitle { background: url("http://www.reboot.ms/forum/styles/arduinoux/arduinoux/circuit_bg_reverted.png") no-repeat scroll 100% center /*[[colora]]*/; }

  /*menu scorciatoie*/
  .navTabs .navTab.Popup.PopupContainerControl.PopupOpen, .navTabs .navTab.selected.PopupOpen .navLink { background-color: /*[[colora]]*/; }

  /*testo*/
  .sidebar .visitorPanel .username, .primaryContent a, #uix_footer_columns h3, .nodeStats.pairsInline dd, .breadcrumb .crust:last-child a.crumb, .sidebar .avatarList .username, a[href]:hover, .discussionListItems .unread .title a, .discussionListItems .unread .lastPostInfo .username, .discussionListItem .stats .major dd, #DiscussionListOptionsHandle a, .footer .choosers a:hover, .navigationSideBar a:hover, #logo .uix_icon, #navigation .uix_textLogo .uix_icon, .statusEditorCounter, .vw-head, .vw-title-box h1 {color: /*[[colorb]]*/ !important; }

  /*testo scuro*/
  .uix_sidePane ul .subMenu .blockLinksList a {color: /*[[colora]]*/ !important; }
  
  /*logo trasparente*/
  img[src*="styles/arduinoux/arduinoux/logo.png"]{
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://i.imgur.com/rPWBaXi.png) no-repeat;
    width: 180px;
    height: 29px;
    padding-left: 180px;
}

  /*bordi*/
  .textCtrl:focus, .textCtrl.Focus, .footer .choosers a:hover, .button:focus, .Menu, .bubbledx { border-color: /*[[colorb]]*/ !important; }
  .pollResult .barContainer { border-color: /*[[colora]]*/ !important; }
  .bubbledx { border: /*[[colorb]]*/ solid 7px !important; }
  .bubbledx:before { border-color: transparent /*[[colorb]]*/ !important; }
  .quote2 { border-left: 15px solid /*[[colorb]]*/ !important; border-right: 2px solid /*[[colorb]]*/ !important; }

  /*barra del progresso*/
  .uploadedFile .Progress .gauge .Meter, .featuresGrid .bx-progress { background: /*[[colorb]]*/; background-color: /*[[colorb]]*/; }
}
