@-moz-document domain("forums.sufficientvelocity.com") {
/* - - - - - - - - - - - - */
/* U S E R - B A N N E R S */
/* - - - - - - - - - - - - */

.userBanner.Director
	{
	background-color: /*[[dirback]]*/ !important;
	border-color: /*[[dirbord]]*/ !important;
	}
.userBanner.Director.wrapped span
	{
	background-color: /*[[dirbord]]*/ !important;
	}
    
.userBanner.ExecDirector
    {
	background-color: /*[[exeback]]*/ !important;
	border-color: /*[[exebord]]*/ !important;
	}
.userBanner.ExecDirector.wrapped span
	{
	background-color: /*[[exebord]]*/ !important;
	}

.userBanner.DepOps
	{
	background-color: /*[[opsback]]*/ !important;
	border-color: /*[[opsbord]]*/ !important;
	}
.userBanner.DepOps.wrapped span
	{
	background-color: /*[[opsbord]]*/ !important;
	}

.userBanner.DepMod,
.userBanner.Mod
	{
	background-color: /*[[modback]]*/ !important;
	border-color: /*[[modbord]]*/ !important;
	}
.userBanner.DepMod.wrapped span,
.userBanner.Mod.wrapped span
	{
	background-color: /*[[modbord]]*/ !important;
	}

.userBanner.DepApp,
.userBanner.Magistrate
	{
	background-color: /*[[appback]]*/ !important;
	border-color: /*[[appbord]]*/ !important;
	}
.userBanner.DepApp.wrapped span,
.userBanner.Magistrate.wrapped span
	{
	background-color: /*[[appbord]]*/ !important;
	}

.userBanner.Council
	{
	background-color: /*[[counback]]*/ !important;
	border-color: /*[[counbord]]*/ !important;
	}
.userBanner.Council.wrapped span
	{
	background-color: /*[[counbord]]*/ !important;
	}
    
.userBanner.Sponsor
	{
	background-color: /*[[sponback]]*/ !important;
	border-color: /*[[sponbord]]*/ !important;
	}
.userBanner.Sponsor.wrapped span
	{
	background-color: /*[[sponbord]]*/ !important;
	}
    
.userBanner
	{
	margin-top: 6px !important;
	}
    
.staff .messageUserBlock
	{
	background-color: #393939 !important;
	}
}

@-moz-document domain("forums.sufficientvelocity.com") {
/* - - - - - - - - - - - - - */
/* C U S T O M - T I T L E S */
/* - - - - - - - - - - - - - */

.userBanner.DepOps.wrapped strong,
.userBanner.DepOps strong
	{
	display: none;
	}
.userBanner.DepOps.wrapped:after,
.userBanner.DepOps:after
	{
	content: "Deputy of Operations";
	}

.userBanner.DepMod.wrapped strong,
.userBanner.DepMod strong
	{
	display: none;
	}
.userBanner.DepMod.wrapped:after,
.userBanner.DepMod:after
	{
	content: "Deputy of Moderation";
	}

.userBanner.DepApp.wrapped strong,
.userBanner.DepApp strong
	{
	display: none;
	}
.userBanner.DepApp.wrapped:after,
.userBanner.DepApp:after
	{
	content: "Deputy of Appeals";
	}
}
