css:
@-moz-document domain("diasp.org") {
.span-5#leftNavBar {
  float: left;
  width: 100% !important;
  margin-left: -10px;
  padding-left: 10px;
  padding-bottom: 10px;
  border-left: 1px solid #ddd;
}
  
#home_user_badge {
  width: 140px;
  height: 140px;
  float: left;
  background: url('/*[[avatar_large]]*/') no-repeat;
  background-size: 140px 140px;
}


ul#stream_selection > li:nth-child(4)  {
  display: none;
}

#home_user_badge .avatar,   
#home_user_badge h4 {
  display: none;
  visibility: hidden;
}
  
#stream_selection {
  float: right;
}

.span-5.rightBar.last {
  display: none;
  visibility: hidden;
}
      
.span-17 {
  width: 950px;
}
}
