css:
@-moz-document url-prefix("http://www.google.com/calendar/"), url-prefix("https://www.google.com/calendar/"), url-prefix("http://calendar.google.com/calendar/"), url-prefix("https://calendar.google.com/calendar/") {

/* Convert Google Bar to hover dropdown */
#gb {
  position: fixed !important;
  height: 25px !important;
  width: 100% !important;
  padding-top: 20px !important;
  padding-bottom: 40px !important;
  top: -110px !important;
  border-bottom: #f1f3f4 15px solid !important;
  margin-top: 25px !important;
  transition: top .3s !important;
}
#gb:hover {
  position: fixed !important;
  top: -45px !important;
}

/* Expand main page and nudge down */
.pSp5K {
  height: 106% !important;
  margin-top: 8px !important;
}
/* Hides add button */
.wJGlHe {
  display: none !important;
}

/* Make Left Menu show on hover */
.QQYuzf {
  width: 0px !important;
  transition: width .3s !important;
  transition-delay: .1s !important;
  border-right: 20px solid #f1f3f4 !important;
}

.QQYuzf:hover {
 width: 260px !important;
}

/* Make Month View month easier to see */
.avfuie {
  margin-top: -4px !important;
  padding: 4px !important;
  font-size: 120% !important;
  color: white !important;
  background: grey !important;
}


}
