@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("(?!.+tbm=isch)https?://(www\.google\.co\.jp|google\.com)/.*"){

    /* color search results */

    .g {
       background: #D4ECEA !important;
        border: 1px double #A2D7D4 !important;
       padding:5px !important;
    }
    
    /* widen search results */

    div#res.med
    {
        width: 130% !important;
    }

    div.s {
        max-width: 100% !important;
    }

    #rhs{
       display: none !important;
    }

}
