css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {

       /* бегунок */
   .html5-scrubber-button, .ytp-scrubber-button {background: #333 !important;border-color: rgba(0, 0, 0, 0.5) !important;box-shadow: 0 0 4px rgba(/*[[scroll]]*/ /*[[scroll2]]*/ 0.9) !important;}
       /* бегунок  при наведени*/ 
   .html5-scrubber-button:hover, .ytp-scrubber-button:hover {background: #333 !important;border-color: rgba(/*[[scroll]]*/ /*[[scroll2]]*/ 0.5) !important;box-shadow: 0 0 9px rgba(/*[[scroll]]*/ /*[[scroll2]]*/ 0.9) !important;}       
       /* нижний бар */
   .html5-player-chrome {background-color: #000 !important; background: linear-gradient(to bottom, #000 30%, #222 90%, #000 50%) !important;background: -webkit-linear-gradient(to bottom, #000 30%, #222 90%, #000 50%) !important}
       /* загрузка < */
   .html5-play-progress, .ytp-play-progress {background: linear-gradient(to right  , rgba(0, 0, 0, 0.95) 15%, rgba(/*[[scroll]]*/ /*[[scroll2]]*/ 0.65) 100%)!important;background: -webkit-linear-gradient(to right  , rgba(0, 0, 0, 0.95) 15%, rgba(/*[[scroll]]*/ /*[[scroll2]]*/ 0.65) 100%)!important}        
       /* загрузка > */
   .html5-play-progress,.ytp-load-progress {background: rgba(0, 0, 0,  0.9)!important; box-shadow: 0px 0px 1px rgba(/*[[scroll]]*/ /*[[scroll2]]*/  .9 )inset !important}       
       /* загрузка */
   .ytp-unloaded-progress {background: rgba(0, 0, 0,  0.9)!important}   
       /* 10 сек */
   .ytp-ad-progress {background: #000 !important; background: linear-gradient(to bottom, #000 0%, #333 100%, #000 190%) !important;background: -webkit-linear-gradient(to bottom, #000 0%, #333 100%, #000 190%) !important}            
       /* бегунок звука */
   .ytp-volume-slider-foreground {background: rgba(/*[[scroll]]*/ /*[[scroll2]]*/  0.4)!important; box-shadow: 0px 0px 5px rgba(0, 0, 0,  .9 )inset !important}    
       /* звук > */ 
   .ytp-volume-slider-foreground:after {background: rgba(/*[[scroll]]*/ /*[[scroll2]]*/ 0.4)!important; box-shadow: 0px 0px 5px rgba(0, 0, 0,  .9 )inset !important}  
       /* звук < */
   .ytp-volume-slider-foreground:before {background: rgba(/*[[scroll]]*/ /*[[scroll2]]*/ 0.4)!important; box-shadow: 0px 0px 1px rgba(0, 0, 0,  1.0 )inset !important}  
       /*цвет текста*/
   .ytp-time-display, .ytp-time-current, .ytp-time-separator, .ytp-time-duration {color: /*[[color]]*/!important}
/*[[button]]*/}
