css:
@-moz-document url-prefix("https://www.facebook.com/") {
body {
background-color: transparent !important; 
background-image: url("http://i.imgur.com/LBJBQrB.png") !important;
background-attachment: fixed !important;
background-repeat: no-repeat !important;
background-position: top center !important;
background-color: #5f83bc !important;
     }

.spinePointer, div.fbTimelineSideAds, #pageFooter {display: none}

._5vb_, ._5vb_ #contentCol {background-color: #ffffff}

._55y4 .navHeader, ._55y4 .navHeader a {color: #ffffff}

._5vb_ ._55y4 ._bui ._5afe:hover, ._5vb_ ._55y4 ._bui ._5afe:focus, ._5vb_ ._55y4 ._bui ._5afe:active, ._5vb_ ._55y4 ._bui ._5afd ._5afe {background-color: #ffffff}

._55y4 .homeSideNav {background-color: #ffffff}

.fbTimelineScrubber {padding-top: 35px !important}

.fbTimelineScrubber {background-color: transparent !important}

.widePage #blueBar #pageHead {margin-left: auto; margin-right: auto; width:60%; }

.fbTimelineUnit:hover {
-webkit-transform:scale(1.1);
box-shadow:0px 0px 30px gray;
opacity: 1;
background-color: #fff;
}


#content, .fbTimelineScrubber {margin-top: 50px !important;}


.uiMediaThumb {
	opacity: 0.8;
	filter: alpha(opacity=80);
 	-webkit-transition: opacity 0.5s linear;  }	
 
.uiMediaThumb:hover {
	opacity: 1;
	filter: alpha(opacity=100);
 	-webkit-transition: opacity 0.5s linear;
        -webkit-transform:scale(1.1);
        box-shadow:0px 0px 30px gray;	}


#mainContainer{
border: 8px solid white !important;
background-color: #fff;
border-radius: 15px; }

.profile-picture:hover, .profilePicThumb:hover {
-webkit-transform: scale(1.4);
-webkit-transition-duration: 0.5s;
position: relative;
z-index: 5;
}


.profile-friends li:hover {
-webkit-transform: scale(1.1);
position: relative;
z-index: 5;
}

.event_profile .event_upload_image {
    width: 179px;
border: 8px solid white !important;
-webkit-transform: rotate(-2deg);
margin-left: -40px;
}
}
