@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myanimelist.net") {  
  
  html {background: rgba(0,0,0, .05)url(/*[[URL]]*/)!important; 
  background-color: #111 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;
  font-family:Constantia!important} 
  
  
   /* background & border remower */  
  #menu_left, #nav li, #nav li a, #horiznav_nav ul li, .bgColor1, .forum_boardrow2, #profileRows a:hover, .profileRows a:hover, .goodresult, .successQuery  {background:transparent!important}  
  *{border:none!important}
  
    
  /* box & shadow */  
  #menu {background: rgba(0, 0, 0,  0.8)!important; box-shadow: 0px 0px 5px rgba(0, 0, 0,  1 )inset !important;border-radius: 20px 20px 0px 0px !important;padding:5px 0px 5px 0px!important}  
  #nav li a:hover{background: rgba(70, 190, 255,  0.2)!important; box-shadow: 0px 0px 5px rgba(0, 0, 0,  1 )inset !important;border-radius:20px!important;color:#39f!important}
  #contentWrapper>h1 {background: rgba(0, 190, 255,  0.4)!important; box-shadow: 0px 0px 19px rgba(0, 0, 0,  1 )inset !important;border:none!important;color: #3ce !important;text-align:center!important;padding:50px !important;font-size: 35px!important;font-family:Constantia!important}
  #contentWrapper>h1:before {content: " /*[[before]]*/ " !important}
  #contentWrapper>h1:after {content: " /*[[after]]*/ " !important}   
  #contentHome, #content  {background: rgba(0, 0, 0,  0.8)!important; box-shadow: 0px 0px 19px rgba(0, 0, 0,  1 )inset !important;border:none!important;border-radius: 0px 0px 20px 20px !important} 
  #footer-block {background: rgba(0, 0, 0,  0.8)!important; box-shadow: 0px 0px 19px rgba(0, 0, 0,  1 )inset !important;border:none!important;border-radius:150px!important}  
  .bg-color-blue1, .episode_list:nth-of-type(2n+1), .forum_boardrow1, .forum_category  {background: rgba(0, 200, 255,  0.1)!important; box-shadow: 0px 0px 8px rgba(0, 0, 0,  1 )inset !important;border-radius:0px 50px 50px 0px!important}
  .bgColor2, .badresult {background: rgba(0, 190, 255,  0.2)!important; box-shadow: 0px 0px 19px rgba(0, 0, 0,  1 )inset !important}
  .header-menu-profile li a {background: rgba(0, 0, 0,  0.6)!important}
  
  
  /* button */  
  #header-menu .header-menu-login .btn-signup, #header-menu .header-menu-login .btn-login, #horiznav_nav ul li a{background: linear-gradient(to right bottom , rgba(0, 0, 0, 0.85) 45%, rgba(155, 155, 155, 0.75) 100%)!important;border-radius: 20px 5px !important;border:none!important}  
  #header-menu .header-menu-login .btn-signup:hover, #header-menu .header-menu-login .btn-login:hover, .button_add:hover, #horiznav_nav ul li a:hover, .inputButton:hover, #profileRows a:hover, .profileRows a:hover, .button_form:hover {box-shadow: 0 0 8px #3cf!important}  
  .button_add, #footer-block .footer-social-media:after, .inputButton, .button_form {background: rgba(0, 0, 0, 0.85) !important;border-radius: 5px !important;box-shadow: 0 0 2px #3cf!important}  
  #horiznav_nav ul li .horiznav_active {background: linear-gradient(to right bottom , rgba(0, 0, 0, 0.85) 45%, rgba(155, 155, 155, 0.75) 100%)!important;border-radius: 20px 5px !important;border:none!important;box-shadow: 0 0 8px #3cf!important} 
 
  /* search & textarea */  
  #topSearchText, .inputtext, #device_type, #inquiry_type, #content>div>form>table>tbody>tr>td>textarea {background-color: rgba(0, 195, 255, 0.19)!important; color: #3ce!important;box-shadow: 0px 0px 3px rgba(0, 0, 0, .9 ) inset!important;border: none !important;border-radius:10px!important;padding:6px!important}  
  #topSearchText:hover, .inputtext:hover, #device_type:hover, #inquiry_type:hover {box-shadow: 0px 0px 5px rgba(155, 155, 155, .9 ) inset!important}
  
   
  /* text color */   
  #nav li a, .header-menu-login .btn-signup, a, .normal_header, .fn-grey3  {color: /*[[1]]*/ !important; text-decoration: none !important}  
  a:hover {color: /*[[2]]*/ !important; text-decoration: none !important}  
  p, .spaceit_pad, .lightLink, .lightLink:link, .lightLink:visited, .lightLink:active, div.spaceit {color: /*[[3]]*/ !important; text-decoration: none !important}  
  div.borderClass>div, #footer-block .footer-social-media:after, h2, h3, #content>div>table>tbody>tr>td, td, .fn-grey2, .fn-grey1 {color: /*[[4]]*/ !important; text-decoration: none !important}  
 .dark_text, .dark_text:hover, .dark_text:visited, span, #content>div, #content>form>div, li  {color: /*[[5]]*/ !important; text-decoration: none !important}  
  
  
  /* ico */  
  #contentWrapper>h1 {background: url(http://savepic.org/7221999.png) no-repeat scroll  rgba(0, 0, 0,  0.8) !important; background-size: contain !important;background-position: left !important}  
  .hoverinfo {background: url(http://savepic.net/6826667.png) no-repeat scroll transparent !important; background-size: contain !important}  
  #headerSmall {background: url(/*[[logo]]*/) no-repeat scroll  transparent !important; background-size: contain !important;background-position: center center !important;transform:scale(4,4) !important;margin: 50px!important}   
   
  
  /* ads */  
    .container>div:nth-child(1), #div-gpt-ad-2701295, #div-gpt-ad-118960287, #div-gpt-ad-897398657, #div-gpt-ad-450660219, .GoogleActiveViewClass, #roadblock, #_CI_widget_1, #homeAds, #IL_INSEARCH, #_bapw-link, #ingageunit, .adHome300, *[id*=div-gpt-ad], div.clearfix:nth-child(21) {display:none!important}
  /* #block-social-button {display: none;} */
  
  
 
}
