@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animespirit.ru") {  
    
   body,html {background: rgba(0,0,0, .05)url(/*[[URL]]*/)!important;   background-color: #111 !important;	background-attachment: fixed !important;	background-clip: border-box !important;	background-origin: padding-box !important;	background-position: center center !important;	background-repeat: no-repeat !important;	background-size: cover !important}   
  *{font-family:/*[[font]]*/!important}
  
  /* background & border remower */  
  .search-block-in, #nav li a.top_link, #nav li a.top_link span, .block-title h3, .block-title h2, .content-block-title h2, .content-main-block, #footer div, #header div div, #header div, #header div div #logo, .accordion h3, .text_spoiler, #ssContent1, .a_block_22, .a_block_61, #dle-content>table>tbody>tr>td>table>tbody>tr>td, #dle-content > table>tbody>tr>td, .content-block-title h3, .a_block_12, .news, .a_block_63, #userinfo>table>tbody>tr>td, #userinfo>table>tbody>tr>td>table>tbody>tr>td {background:transparent!important}  
  *{border:none!important}  
  
  /* box & shadow */  
  #nav {background: rgba(0, 0, 0,  0.8)!important;border-radius: 0px 0px 25px 25px !important; box-shadow: 0px 0px 8px rgba(0, 190, 255, .9 ) inset!important}  
  li.top>ul, #nav li:hover li:hover ul, #nav li:hover li:hover li:hover ul, #nav li:hover li:hover li:hover li:hover ul, #nav li:hover li:hover li:hover li:hover li:hover ul, #nav li:hover ul.sub li a.fly:hover, a.fly, #nav li:hover ul.sub li a:hover  {background: rgba(0, 0, 0,  0.65)!important}   
  .block-title, .content-block-title, table.tracklist th {background: rgba(0, 190, 255,  0.15)!important}  
  .search-block {background: rgba(0, 0, 0,  0.8)!important;border-radius: 0px 0px 25px 25px !important}  
  .block, .content-block {background: rgba(0, 0, 0,  0.8)!important;border-radius: 0px 0px 15px 15px  !important}  
  .title_spoiler {background: rgba(0, 190, 255,  0.05)!important; box-shadow: 0px 0px 8px rgba(0, 0, 0, 1 ) inset!important}  
  .an_alert {background: rgba(255, 0, 0,  0.2)!important;box-shadow: 0px 0px 200px rgba(0, 0, 0, 1 )inset!important;border-radius:15px!important;color:#f55!important}  
  #dle-content>table {background: rgba(0, 0, 0,  0.4)!important;box-shadow: 0px 0px 200px rgba(0, 0, 0, 1 )inset!important;border-radius:15px!important}   
  table.tracklist tr td, .defaultSkin table {background: rgba(0, 200, 255,  0.05)!important} 
  
  
  /* search & textarea */
  .s_field {background: rgba(0, 0, 0, 0.25)!important; color: #3ce!important;box-shadow: 0px 0px 3px rgba(0, 215, 255, .9) inset!important;border: none !important;border-radius:5px!important;padding:6px!important}  
  .s_field:hover, .a_field:hover, #loginToSearch:hover {box-shadow: 0px 0px 8px rgba(0, 215, 255, .9) inset!important}  
  .a_field, #loginToSearch {background: rgba(0, 0, 0, 0.25)!important; color: #3ce!important;box-shadow: 0px 0px 3px rgba(0, 215, 255, .9) inset!important;border: none !important;border-radius:5px!important;padding:3px!important}  
  
  /* button */  
  .s_button {background: linear-gradient(to right bottom , rgba(0, 0, 0, 0.85) 45%, rgba(155, 155, 155, 0.75) 100%)!important;border-radius: 5px !important;border:none!important;color:#3ce!important}  
  .s_button:hover, .navigation a:hover {box-shadow: 0 0 8px #3cf!important}  
  .navigation span, .navigation a:link, .navigation a:visited, .navigation a:active {background: rgba(0, 0, 0, 0.65)!important;border-radius: 3px !important;color:#3ce!important}  
  .accordion h3:hover {background: rgba(0, 190, 255,  0.15)!important; box-shadow: 0px 0px 30px rgba(0, 0, 0, 1 ) inset!important}   
  
  /* text color & decoration */  
  html, body, a, .search-block h2, #nav li a.top_link, .ltitle_small, .title_spoiler, .slink  {color:/*[[1]]*/!important;text-decoration:none!important}  
  a:hover, #nav li a.top_link:hover, .content-block-title h2, .news   {color:/*[[2]]*/!important;text-decoration:none!important}    
  
  /* ico */   
  #header {background: url(/*[[logo]]*/) no-repeat scroll  transparent !important; background-size: contain !important;background-position:center bottom!important}   
  #footer {background: url(/*[[footer]]*/) no-repeat scroll  transparent !important; background-size: contain !important;background-position:center bottom!important}   
    
  /* ads */  
  #footer > div:nth-child(1) > img:nth-child(1), #google_ads_div_Animespirit-240x400-left_ad_wrapper, #google_ads_div_Animespirit-240x400-left-bottom_ad_wrapper, #google_ads_div_Animespirit-240x400-right_ad_wrapper, #google_ads_div_Animespirit-240x400-right-bottom_ad_wrapper, #k_inline_div_31431    
    {display:none!important}
 
  

}
