css:
@-moz-document domain("tweetdeck.twitter.com") {
p > a[data-user-name="/*[[username]]*/"]  {
    color: /*[[textcolor]]*/;
    border: /*[[border]]*/ /*[[bordercolor]]*/;
}
.is-inverted-dark a[data-user-name="/*[[username]]*/"] {
    color: /*[[textcolor]]*/;
    border: /*[[border]]*/ black;
}
}
