css:
@-moz-document domain("duckduckgo.com") {


  .site-wrapper-border{
    visibility: collapse;
  }


  #search_form{
    margin-right: 0;
  }


/* Zero Click Wrapper :: About Products Images Videos Meanings News ...  */
  .zci__metabar{
    padding-left: 20px;
  }
  .tileview .zci__metabar__primary-text{
    padding-left: 10px;
  }
  .zci__main{
    padding-left: 15px;
  }
  .zci__main.has-tiles{
    padding-left: 74px;
    padding-left: 68px;
  }



/* Region switch */
  .results--sidebar{
    top: 1px;
  }
  .region-switch{
    padding: 0 0 0 14px;
  }
/*[[REGION]]*/
/******************************/

  /*.set-super-wide .detail__body__content,
  .set-super-wide .c-info,
  .set-super-wide .c-base,
  .set-super-wide .c-icon,
  .set-super-wide .c-list,
  .set-super-wide .zci__main.has-aux,
  .set-super-wide .zci__main--answer, */
  .set-super-wide .results, /*
  .set-super-wide .results--didyas,
  .set-super-wide .results--message,
  .set-super-wide .results--ads, */
  .set-super-wide .results--sidebar--mid,
  .set-super-wide .cw {
    max-width: 100%
  }
  /*.set-wide .detail__body__content,
  .set-wide .c-info,
  .set-wide .c-base,
  .set-wide .c-icon,
  .set-wide .c-list,
  .set-wide .zci__main.has-aux,
  .set-wide .zci__main--answer, */
  .set-wide .results, /*
  .set-wide .results--didyas,
  .set-wide .results--message,
  .set-wide .results--ads, */
  .set-wide .results--sidebar--mid,
  .set-wide .cw {
    	max-width:100%;
  }
  /* .detail__body__content,
  .c-info,
  .c-base,
  .c-icon,
  .c-list,
  .zci__main.has-aux,
  .zci__main--answer, */
  .results, /*
  .results--didyas,
  .results--message,
  .results--ads, */
  .results--sidebar--mid,
  .cw {
    	max-width:100%;
  }
  .set-wide .detail__body__content,
  .set-wide .c-info,
  .set-wide .c-base,
  .set-wide .c-icon,
  .set-wide .c-list,
  .set-wide .zci__main.has-aux,
  .set-wide .zci__main--answer,
  .detail__body__content,
  .c-info,
  .c-base,
  .c-icon,
  .c-list,
  .zci__main.has-aux,
  .zci__main--answer{
  	max-width:1152px;
  }

/* BACK TOP */
  .results--sidebar--mid {
    position: fixed;
  }
  .results--sidebar--mid{
    max-width: 100%;
    padding-left: 0px;
    z-index: 15;
  }
  .results--sidebar--mid .btn--top {
    right: 5px;
    opacity: .67;
  }


/* Content Wrapper */
  .cw, .cw--c{
    max-width: 100%;
    padding: 0;
  }

  .results-wrapper {
    padding: 15px 0px 0 20px;
  }


  .cw .results--didya, .cw--c .results--didya{
    padding-top: 0;
  }
  .results--didyas{
    margin-top: 0;
  }
  .cw .results--didyas, .cw--c .results--didyas {
    margin-bottom: 0;
  }

  .results--message{
    margin-bottom: 0;
    padding-top: 0
  }


  .result--ad{
    padding-top: .2em;
  }


  .results {
    max-width: 100%;
    padding-bottom: 0;
  }

  #links .result {
    float: left;
    clear: none !important; /* undo SearchPreview style setting! */
  }
  #links .no-results{
    display: inline-block;
  }
  #links .result--sep.result--sep{
    display: inline-block;
    margin-left: 2.5%;
  }
  #links #loading.result--more{
    float: left;
    box-sizing: border-box;
    height: 155px;
    width: 76px;
    line-height: 140px;
    padding: 0;
    margin: 0 .667em .667em 0;
    border-color: transparent;
  }

/*[[COLUMNS]]*/
/********************/

  #links .results_links.highlight,
  #links .results_links_deep.highlight{
    border-radius: 0
  }

/* Result Highlight Indicator */
/*[[HIGHLIGHT]]*/
/***********************************************/

/* Results Counter */
  #links .results_links,
  #links .results_links_deep{
    counter-increment: myIndex;
  }
  #links .results_links_deep::after,
  #links .results_links::after{
    content: counter(myIndex, decimal-leading-zero);
    position: absolute;
    top: 4px;
    left: 4px;
    min-width: 16px;
    height: 16px;
    border: 1px solid;
    border-radius: 8.5px;
    font-size: 10px;
    text-align: center;
    line-height: 16px;
  }
/*[[COUNTER]]*/
/************************************************/

/* Results Checkmark (Visited Indicator) */
  .result__check:before{
    width: 10px;
  }
  .result__check:before{
    width: auto;
    opacity: .75;
  }
  .result__check:link:focus,
  .result__check:link:hover{
    color: #B47F3A;
  }
/*[[VISITED_MARK]]*/
/************************************/

/*[[NARROW_VIEW_FIX]]*/
  .result__a{
    white-space: nowrap;
    overflow: hidden;
    -ms-text-overflow: Ellipsis;
    -o-text-overflow: Ellipsis;
    text-overflow: Ellipsis;
  }
  .result__snippet {
    max-height: 6.95em;
    max-height: 92px;
    overflow: hidden;
  }

/*** Snippet Highlights ***/
/*[[SEARCH_KEYS]]*/
/*************/


  .result--ad .result__extras__url{
    max-width: 100%;
  }

  .result:not(.result--ad).highlight .result__extras__url{
    width: auto;
    max-width: 100%;
    padding-right: 6.6em;
    margin-right: -6em;
  }
  .result:not(.result--ad).highlight .result__extras__url:last-child{
    padding-right: 0;
    margin-right: 0;
  }
  .result .result__menu{
    width: 6em;
  }
  .result--url-above-snippet .result__menu {
    margin-left: 0;
  }

  .full-urls .result__extras__url {
    padding-right: 0;
  }


  #links .result.result--sep{
    height: 1.5em;
    width: 95%;
    border: none;
    margin-top: 0;
    margin-right: auto;
    padding: 0;
  }

  .no-results {
    padding-top: 7px;
    width: 100%;
    padding-left: 0.75em;
  }
  .no-results > div > a {
    margin-left: 1em;
  }


  #links .result.result--more {
    padding: 4px 2px ! important;
  }


  #bottom_spacing2 {
    padding-bottom: 3em;
  }


}
