css:
@-moz-document domain("www.pandora.com") {
/* If you would like to create your own Pandora style using mine as a base, please credit my style in a comment in your CSS or Additional Info section! https://userstyles.org/styles/114346/pandora-minimal-player */
body {
	background-image: none;
	background-repeat: no-repeat;
	background-position: 50% 50%;
	background:
		linear-gradient(
			to top right,
			rgba(18,142,212,0.57) 0%,
			rgba(36,156,221,0) 50%,
			rgba(70,116,235,0.3) 75%,
			rgba(24,53,211,0.8) 100%
		),
		linear-gradient(
			to bottom right,
			rgba(13,65,175,0.5) 0%,
			rgba(19,101,193,0.5) 25%,
			rgba(94,193,255,0.4) 53%,
			rgba(63,175,241,0.6) 70%,
			rgba(88,92,252,1) 95%,
			rgba(108,92,251,1) 100%
		);
	background-size: cover;
	background-color: #0E77CC;
	background-attachment: fixed;
}

#topnav {
 	height: 100%;
}

#playerBar {
	background-color: rgba(0,0,0,0.25);
	border-color: transparent;
	min-width: 0;
	height: 100%;
}

#playerBar .columns {
	display: table;
	margin: 0 auto;
	width: 100%;
}

#playerBar .rightcolumn {
	display: table-header-group;
	float: none;
	padding: 0;
	text-align: center;
	width: 100%;
}

#playerBar .middlecolumn {
	display: table-footer-group;
}

#playerBar .middlecolumn .buttons {
 	width: 430px;
}

#playerBar .progress {
	width: auto;
	max-width: calc(100vh - 180px);
}

#playerBar .progress .remainingTime {
	padding-right: 6px;
}

#playerBar .progress .progressBar {
	margin: 0 auto;
}

#playerBar .thumbDownButton {
	margin: 6px 19px 0 100px;
}

#playerBar .nowplaying {
	display: table !important;
	margin: 12px auto;
	text-align: center;
	width: 100%;
	max-width: 800px;
}

#playerBar .rightcolumn .albumArt {
	margin: 0 auto;
	width: calc(100vh - 180px);
	height: calc(100vh - 180px);
	max-width: 100vw;
	max-height: 100vw;
	
}

#playerBar .rightcolumn .cd_icon {
	float: none;
	margin: 0;
	max-width: 100vw;
	max-height: 100vw;
}

#playerBar .playerBarArt {
	box-shadow: 0px 0px 15px 5px rgba(0, 0, 0, 0.25);
	position: relative;
	height: 100%;
	width: 100%;	
}

#playerBar .rightcolumn .info {
	display: table-header-group;
	float: none;
	text-align: center;
	width: auto;
	text-shadow: 0px 0px 15px rgba(0, 0, 0, 0.75);
}

#playerBar .rightcolumn .info div:last-child {
	padding-bottom: 16px;
}

#playerBar .rightcolumn .info div {
	height: 24px;
}

#playerBar .rightcolumn .info a {
	font-size: 20px;
}

.skinContainer, .dropdownmenu, .leftcolumn, .volumePosition, #brandingBar, #footer, .playerByText, .playerOnText {
	display: none !important;
}
}
