css:
@-moz-document domain("archiveofourown.org") {
html { font-size: /*[[BASEFONTSIZE]]*/; }
#outer { font-size: 1em; }
*, *:focus { outline: none !important;}

/* fonts */
#MAIN *, body, blockquote, pre, #header a, input, .actions a, .datetime, .meta, .autocomplete, h1,h2,h3,.heading, h4,h5, .dropdown, .secondary, span.unread, .replied, span.claimed, .actions span.defaulted, .toggled form, .dynamic form, textarea, span.symbol, span.question,select,button { font-family: /*[[BASEFONT]]*/ ,helvetica neue,segoe ui,sans-serif !important; }
kbd, tt, code, var, pre, samp { font-family: menlo,consolas,monospace !important; }
blockquote, pre { font-weight: normal; }
#main em, #main cite, #main i { font-style: italic; }
#header .menu .current,h6,.lv_validation_message,span.delete,.important,a.cloud8 { font-weight: bold !important; }
 
#workskin [role="article"] div.userstuff,
#workskin .userstuff[role="article"] { font-family: /*[[WORKFONT]]*/ !important; font-size: /*[[WORKFONTSIZE]]*/ !important; line-height: 1.5; }

#workskin { max-width: /*[[WORKWIDTH]]*/; }

#outer .tip { font-size: 0.8rem !important; border: none; padding: 0.643em !important; }
.search input:focus+[role="tooltip"] { left: 0; right: 0; top: 2rem; }

.important { font-weight: bold; }

/* main */
#main * { text-shadow: none  !important; }
.group { box-sizing: border-box; }

body, #main { background: #fafafa; }
#inner, body.javascript { background: #f0f0f0!important; }
#main, .splash h3.heading, .filters .expander {color: #111; }

#main {padding-top: 2em; }
#main.dashboard { margin-top: 0 !important; margin-bottom: 0 !important; }
.home .primary { overflow: auto; }

#header, .dropdown, .wrapper, .secondary, fieldset, #outer #header .primary, .notice, ul.notes, form dl, form.verbose legend, .verbose form legend, .listbox .index, #header fieldset, .error, .comment_error, .caution, .delete a, span.delete, .toggled form, .dynamic form, .LV_invalid, label, .action, input[type="checkbox"], .notice * { box-shadow: none !important; }

.datetime { font-size: 85%; color: gray; font-weight: normal; }
.meta span.series .divider { color: gray; } 
.userstuff blockquote { padding: 0 0.75em; }

/* headings */
h1, h2, h3 { word-break: normal !important; display: block; }
.heading { font-weight: bold; word-break: normal !important; }
.splash h3.heading { font-size: 1rem; padding-bottom: 0.643em; }
.heading, .listbox .heading a, .collections,h2.heading, #header .collections  { font-weight: normal; }
.home .header h2 { border-bottom: none; }
h2 { font-size: 1.9rem; }
h3 { font-weight: bold; }
.userstuff h3 { border-color: gainsboro !important; padding: 0.643em 0; margin: 0.643em 0; border-style: solid !important; border-bottom-width: 1px; }
.preface .module h3 { border-bottom: 1px solid gainsboro !important; padding: 0.643em 0; }
.heading a { border-bottom: 0; }

/* links */
a, a:link { border-bottom:none; }
a, a:link, #main .blurb h5.heading a.tag, .blurb h4 a:link { color: /*[[COLOR]]*/; }
a:visited, a:visited:hover { color: #555; border-bottom:none; }
div.preface .byline a:hover,
a:hover { text-decoration: underline; }
div[id=main] a { font-weight: bold; }
a:focus { outline: none; }

.filters dt a:hover { border-bottom: none !important; }

/* tags */
#main a.tag { border-bottom: 1px dotted; color: dimgray; font-weight: normal; }
#main li.relationships a.tag { background: transparent; color: black; font-weight: bold; }
#main .tags strong a { font-weight: normal; }
#main a.tag:hover, #main .blurb h5.heading a.tag:hover { background: transparent; color: black; text-decoration: none !important; box-shadow: none; border-bottom: 1px solid !important; }
#main .tag.added, #main .tag .delete a, #main .tag .delete a:hover  {border-bottom: none !important; }
.blurb a.tag { line-height: 1.3; }


/* header */
#header, #header .primary, #header .menu, #small_login, #header .dropdown:hover .current + .menu {background: white; }

#header .primary li { margin: 0; }
#header a { outline: none !important; }
#header { margin-bottom: 0; border-bottom: 1px solid gainsboro; }
#header .primary { height: auto; border-top: 1px solid gainsboro;  }
#header .primary a, #header #greeting .user a { color: dimgray; text-decoration: none !important; border-radius: 0; background: transparent; }
#header .menu, #small_login, #header .dropdown:hover .current + .menu { box-shadow: none; min-width: 10em !Important; width: auto !important; }
#greeting p.icon {padding: 0.2em 0 0 0; }
#login .user a { float: right; }
#small_login { line-height: normal; }
#small_login .actions input[type="checkbox"] {height: auto; vertical-align: baseline; }
#small_login .login .action { padding: .45em .7em; }
#header .menu, #small_login { border: 1px solid silver; box-shadow: 0 5px 15px rgba(0,0,0,0.1); }
#header .primary li.dropdown:first-of-type { margin-left: 1em; }
#header .primary a { font-size: 0.88rem; padding: 0.9em 0.6em; box-sizing: border-box; height: auto; }
#header #greeting .menu a, #header .primary .menu a, #header  .menu.dropdown-menu a, #header .menu.dropdown-menu .current { color: #333 !important; padding: 0.5em 0.8em; font-size: 0.88rem; text-decoration: none !important; height: auto; }
#header .primary .menu.dropdown-menu a:hover, #header #greeting .menu a:hover {  background: rgba(0,0,0,0.07); color: /*[[COLOR]]*/!important;  text-decoration: none !important; }
#header .primary .dropdown:hover a.dropdown-toggle, #header .open a.dropdown-toggle, #header .dropdown a.dropdown-toggle:hover, #greeting .user .dropdown-toggle:hover, #greeting .user a[href*=logout]:hover { color: /*[[COLOR]]*/!important; z-index: 99; background: transparent; }
#header .dropdown-menu { border-radius: 0 0 3px 3px; border: 1px solid gainsboro; background: white; border-bottom: 3px solid /*[[COLOR]]*/; }
#header .user { padding: 0; }
#header .menu li { border-color: transparent; margin: 0; }
#header .logo { height: 18px; margin: 0.25em 0.5em 0 1em; }
#header h1 { font-size: 1rem; height: auto; color: black; }
#header h1 a:hover { text-decoration: none !important; }
#header h1.heading { padding: 0.2em 0 0 0; }
#header .heading a { font-size: 1rem; font-weight: bold; color: black; }
#header #search #site_search { box-sizing: border-box; border: none; padding:0.25em 0.4em; background: white; margin: 0; height: 1.88rem; width: 15em; margin-right: -3px; }
#header #search .submit { display: inline-block; margin: 0; padding: 0; }
#header #search .button { height: 1.88rem; border-top-left-radius: 0; border-bottom-left-radius: 0; border-left: 1px solid silver; box-shadow: none !important; padding: 0.8em 0.8em; line-height: 0.5em; }
#header .search p { border: 1px solid silver; display: inline-block; border-radius: 2px; margin: 0.4em; }
#header .collections { border-top: 1px solid gainsboro; text-align: center; background-size: cover; background-position: center; background-color: gainsboro; color: /*[[COLOR]]*/; line-height: normal; }
#greeting .user a { padding: 0.5em; }
#header .landmark { display: none; }

/* splash */
.splash .favorite li, .splash .favorite li a, .splash .favorite li:hover  { background: transparent !important; }
.splash .favorite li a { padding: 0; display: inline; }
.splash .favorite li { padding: 0.25em 0.375em;}
#main .splash .favorite li:nth-of-type(2n+1) { background: whitesmoke !important; }
 
 
/* notices */
.flash.notice { margin-bottom: 2em; }
.error, .notice, .example, .caution { padding: 0.643em !important; border-radius: 4px; }
.userstuff .notice, .userstuff .caution, .userstuff .example { width: auto !important; margin: 0.643em auto !important; }
ul.notes { margin: 0.643em 0; }
.notice p:first-of-type { margin-top: 0; }
.notice p:last-of-type {margin-bottom: 0; }
.LV_validation_message { position: relative; display: inline-block; }

/* works */
#workskin { padding: 0 }
#workskin .chapter .preface {border: none; }
#workskin .preface blockquote.userstuff { font-size: 0.95em; }
#workskin .preface { margin: 1em 2%; padding: 0; }
.preface .title { margin-top: 2em; }
.feedback .kudos { line-height: 1.4; font-size: 0.9em;  }
dl.meta { border: 1px solid gainsboro; border-radius: 2px; margin: 0.643em 0;}
div.preface .series ul, div.preface .children ul { padding: 0; }
.preface blockquote { margin-left: 0; margin-right: 0; }
.userstuff img { max-width: 100% !important; }


/* footer */
#footer { background: transparent; border-top: 1px solid gainsboro; color: #111; }
#footer a, #footer a:active, #footer a:visited { color: /*[[COLOR]]*/; border-bottom: none; }
#footer a:hover { text-decoration: underline; }
#footer .actions .heading { color: dimgray; }
#footer ul { width: auto; box-sizing: border-box; margin: auto; overflow: auto; float: none; }
#footer ul li { padding: 0; margin: 0.5em 0; }

/* dashboard */
#dashboard, #dashboard.own { background: transparent; border: 1px solid transparent; margin: 0; border-radius: 0;   padding: 0.5em; }
#dashboard li { margin: 0.2em 0; }
#dashboard a, #dashboard span.current { text-align: left; margin: 0; padding: 0.4em; border-radius: 3px; color: dimgray;text-decoration: none; line-height: 1.3; font-size: 0.86rem; }
#dashboard .secondary a { margin: 0; }
#dashboard .actions a:hover { background: transparent; color: /*[[COLOR]]*/; text-decoration: underline; }
#dashboard .current { background: transparent; font-weight: bold; color:black; }
#dashboard ul { padding: 0.5em 0; border-width: 1px; }
#dashboard ul:first-of-type { border-top: none; }


/* actions */
ul.actions, p.actions, li.actions, span.actions, fieldset.actions, dd.actions { position: relative; margin-right: 0; } 
.comment ul.actions { margin-right: 0.4em; margin-bottom: 0; }
[id="main"] ul.actions { text-align: right; padding-right: 0.4em; }
[id="main"] .actions a:not(.help), [id="main"] .action, [id="main"] .actions input:not(#tag_search):not(#query), input[type="submit"],  [id="main"] .button, [id="main"] .current, [id="main"] .actions label, [id="main"] .actions a:visited, [id="main"] a.action, [id="main"] .action, label.action, input[type="checkbox"], .announcement input[type="submit"], .announcement .action, #modal .action { box-shadow: none !important; background: white!important; color: dimgray; border: 1px solid silver; font-weight: bold!important; font-size: 0.826em; border-radius: 2px; text-decoration: none !important; background-image: none !important; padding: 0.5em 0.7em; height: 2.31em; box-sizing: border-box; line-height: 1.18; }
#main input[type="checkbox"] { height: auto; line-height: 1; vertical-align: text-top; margin: 0 0.4em; }
#main .comment ul.actions a { padding: 0; border: none; box-shadow: none !important; font-size: 0.9em; margin-right: 0.5em; background: transparent !important; height: auto; color: /*[[COLOR]]*/; }
label.action { padding: 0.5em 0.8em; box-sizing: border-box; }
[id="main"] .actions .disabled { padding: 0.5em 0.8em; line-height: 1.286; height: 2.31em; border: 1px solid transparent; vertical-align: middle; box-sizing: border-box; }
[id="main"] a.action:hover, [id="main"] .action:hover, [id="main"] .action a:hover, [id="main"] .actions a:hover, [id="main"] .actions a:focus, input[type="submit"]:hover, input[type="submit"]:focus, .actions input[type="submit"]:focus, label.action:hover, .action input[type="checkbox"]:hover, span.question.symbol:hover, .announcement input[type="submit"]:hover, .announcement .action:hover,
#modal .action:hover { box-shadow: 0 1px 0 rgba(0,0,0,0.08)!important; background: #f1f1f1!important; color: /*[[COLOR]]*/; text-decoration: none !important; border-color: silver;}

#main .actions a:active, #main .action a:active, #main input[type="submit"]:active, #main .actions input[type="submit"]:active, #main .action.top:active, #main .actions input[type="submit"]:focus, #main a.action:active, .top a:active, .top a:focus, span.question.symbol:active, span.question.symbol:focus,  #modal .action:active { background: #eee !important; color: /*[[COLOR]]*/; text-decoration: none !important; border-color: /*[[COLOR]]*/; }
#main .comment ul.actions a:hover, #main .comment ul.actions a:active, #main .comment ul.actions a:focus { background: transparent; text-decoration: underline !important; color: /*[[COLOR]]*/; }
[id="main"] .actions .current { background: #e2e2e2!important; color: dimgray !important; border: 1px solid transparent; border-radius: 2px; }
a.help:hover,
.top a:hover { text-decoration: none !important; background: transparent !important; }
.top a:active, .top a:focus, .top:active a { background: transparent !important; color: /*[[COLOR]]*/ !important; text-decoration: none !important; }
span.question.symbol { border-color: gray; color: black; background: white; box-shadow: none; text-decoration: none !important; vertical-align: baseline; }
span.unread, .replied, span.claimed, .actions span.defaulted { color: #e00; background: transparent; border: 1px solid; border-radius: 2px; }
ol.pagination, div.pagination, ol.year { margin: 1em 0; }
div[id="main"] ul.actions, p.actions, ol.pagination, div.pagination, fieldset.actions ul[role=menu] { line-height: 2.5;  }
.listbox p.actions { margin: 0; }
.actions > *, .stats > *, .meta ul li { padding: 0; }

/* inputs */
input[type="submit"], input.text { height: 2.31em;}
.actions input[type="submit"] { line-height: 0; }
textarea {  padding: 0.643em;}
input[type="text"]:hover, #outer input[type="text"]:focus,input[type="password"]:focus,textarea:hover, textarea:focus, select:hover, select:focus { background: white; box-shadow: inset 0px 1px 2px gainsboro; border-color: dimgray !important; color: #333; border-radius: 2px; }
input, textarea, select, .search input[type="text"], form.search input[type="text"] { border-radius: 2px; box-shadow: inset 0px 1px 2px gainsboro; box-sizing: border-box; border: 1px solid silver; }
select, select[id*=language] { padding: 0.3em; vertical-align: middle; font-size: 0.826em; height: 2.31em; }
input[type="text"],input[type="password"]  { padding: 0.5em 0.8em; vertical-align: middle; }
.filters input[type="text"], .autocomplete input, .autocomplete .dropdown ul li { display: block;  }


/*listboxes, modules, fieldsets */
.comment fieldset, div.comment { border: 0; box-shadow: none; clear: left;}
form dt { padding: 0.5em 0; border-color: gainsboro; }
.listbox, fieldset, fieldset fieldset.listbox, fieldset fieldset fieldset, div.comment fieldset, .toggled form, .dynamic form { border: 1px solid gainsboro; background: whitesmoke; box-shadow: 0 1px 1px whitesmoke; border-radius: 2px; margin: 0.643em 0; }
.search fieldset { display: block; }
#main .listbox, li.comment.unread, fieldset fieldset.listbox, .thread .odd, .thread .even { background: white; }
fieldset dl, form dl { border: 0; margin: 0.643em 0; background: transparent; }
div.comment fieldset, li.comment, .verbose legend { border-color: gainsboro; border-radius: 2px; }
.listbox .listbox { margin: 0.643em; }
fieldset fieldset.listbox { padding: 0.643em; }
.thread .comment fieldset { border-radius: 2px; margin: 0.643em; border: 1px solid silver; }
.thread .even .heading { background: whitesmoke; }
.post.comment .heading { float: left; }
.comment h4.heading {margin: 0; padding: 0.643em; }
.comment div.icon { float: left; margin: 0.643rem; border-bottom: 0; }
.comment h4.byline { background: whitesmoke; border-bottom: 1px solid gainsboro; font-size: inherit; font-weight: bold; }
#main .comment .userstuff { margin-top: 0.643em;  }
#main .comment .landmark { position: absolute; }
.comment .userstuff p { line-height: 1.4; }
.listbox .tags li { margin: 0.3em 0; }
.listbox h3.heading, .listbox .listbox .heading, fieldset .listbox h5.heading { padding: 10px; border-bottom: 1px solid gainsboro; display: block; background: whitesmoke !important; font-size: 1rem; }
#modal-wrap #modal { border: none; border-radius: 4px; width: 90%; }
.comment .edited { margin: .86em; float: left; }

/* dropdowns */
#main .dropdown, #main .secondary, #dashboard .expandable.secondary,
#ui-datepicker-div { background: white; border: 1px solid silver; box-shadow: 0 2px 2px rgba(0,0,0,0.1) !important; }
.chapter .secondary, .download .secondary { right: 0; top: auto; }
.autocomplete .notice { margin: 0px; border-radius: 0; border: none; padding: 0.4em; background: white; }
.autocomplete .dropdown ul li:hover, li.added.selected { background: transparent; color: black; }

.autocomplete div.dropdown ul li, .autocomplete .notice { display: block; width: auto; margin: 0; padding: 0.4em 0.5em; }
.autocomplete div.dropdown ul li:hover, .autocomplete div.dropdown ul li.selected {background: /*[[COLOR]]*/; color: white; }
.autocomplete div.dropdown ul { margin: 0; padding: 0; border:none; box-shadow: none !important; }
#dashboard .secondary { display: block; padding: 0.2em; margin: 0.2em; }

/* datepicker */
.ui-datepicker table, .ui-datepicker th,.ui-datepicker tr:hover, .ui-datepicker td:hover { background: transparent; }
.ui-datepicker td:hover { background: gainsboro!important; }

/* blurbs */
li.blurb, .dashboard .blurb { background: white; border: 1px solid gainsboro; border-radius: 3px; }
li.blurb, .blurb .blurb { padding: 0.75em; }
.blurb blockquote { font-weight: normal; text-align: left; }
.blurb h4.heading a:first-of-type { font-weight: bold; color: black;  }
.blurb h5.heading a.tag { line-height: normal; }
.blurb div.header.module { margin-bottom: 0.375em; }
.blurb .header .heading { margin: 0 5.25em 0px 65px;}
.blurb .header h4.heading { font-size: 1.3em; line-height: 1.3; }
.blurb .header h5.heading { margin-top: 0.2em; }
.blurb .picture .heading { margin-left: 65px !important; }
.reading h4.viewed { background: transparent; font-weight: normal; font-size: 0.8em; }
.blurb dl.stats { float: none; }
.blurb.picture .header { border-bottom: 1px solid gainsboro; }
.blurb .user.module { border: none; border-top: 1px solid gainsboro; margin-top: 1em; padding-top: 0.3em; background: transparent; }
.blurb .user .actions, .bookmark .recent .index {padding: 0; }
.bookmark p.status { margin-top: 0; }
.blurb .user .heading { margin: 0; }
.blurb .user:not(.short) .datetime { margin-top: 0.5em; }
.blurb .actions li { padding: 0; }
.bookmark .user .userstuff { margin-top: 0; margin-bottom: 0; }
.bookmark .user .meta { line-height: normal; }
.bookmark .short { background: transparent; border: none; box-shadow: none; border-bottom: 1px solid gainsboro; border-radius: 0; margin: 0;  }
.bookmark .short .status { left: 0; }
.bookmark .short .header .heading { margin-left: 35px; }
.blurb .userstuff p { line-height: 1.3; }

div.icon, .icon img { border-radius: 4px !important; }
dl.index dd { background: transparent; }
dl.stats dt { padding-left: 0; }
div.preface { margin: 1.5em 0; }

.notice { color: #2B528E; }
.caution { background: #fff583; color: #bb8728; border-color: #f9ce6c; }
.announcement .userstuff {margin: 0.5em; padding: 1%; border-radius: 3px !important;}
.flash form { overflow: auto; }
.fold .actions { padding: 0px; position: absolute; right: 0px; top: 0px;} 
.fold { margin: 0; }
.fold .cut  { margin-top: 1em; border-top: 1px solid gainsboro; padding-top: 0.643em; }

/* styling for "Extension of our own" chrome extension */
.subscriptions-index table,
.default-style tr:nth-child(2n), .default-style tr:nth-child(2n+1) { background-color: transparent !important; }
th, tr:hover, col.name { border: 0px solid transparent !important; }


/* blurb symbols option */
/*[[SYMBOLS]]*/
/*END base css*/
/*[[COLORSCHEME]]*/

}
