css:
@-moz-document domain("boards.4chan.org") {
/*waifus*/
/*[[waifus switch]]*/

body::after
{
    position: fixed !important;
    content: url("http://cdn.syn-ch.com/src/143/44/42/1434442166-28b70.png");
    bottom: -3px !important;
    right: 0px !important;
    opacity: 0.5;
    z-index: -1;
}

body::before
{
    position: fixed !important;
    content: url("http://cdn.syn-ch.com/src/143/44/42/1434442201-7e1f2.png");
    bottom: -3px !important;
    right: 200px !important;
    opacity: 0.8;
    z-index: 0;
}
/*Options*/

div.sideArrows
{
    display: none !important;
}

#bannerCnt
{
    display: none !important;
}

body > form > div:nth-child(5)
{
    display: none !important;
}

body > div:nth-child(15)
{
    display: none !important;
}

body > div:nth-child(6)
{
    display: none !important;
}

.ad-cnt
{
    display: none !important;
}

/*Main*/

span.stylechanger
{
    display: none !important;
}

.deleteform
{
    color: #195527 !important;
}

#bottomReportBtn
{
    display: inline-block !important;
}

.subject
{
    color: #CC9030 !important;
}

.name
{
    color: #842A2D !important;
}

.postertrip
{
    color: #97543A !important;
}

div.sideArrows
{
    color: #279933 !important;
}

.op
{
    color: #195527 !important;
}

.reply
{
    background-color: #040E24 !important;
    color: #17662A !important;
    border: solid 2px #060F36 !important;
}

s
{
    background-color: #000 !important;
    color: #195527 !important;
}

s a:not(:hover)
{
    background-color: #000 !important;
    color: #000 !important;
}

a
{
    color: #7C6142 !important;
}

.quotelink
{
    color: #7C6142 !important;
}

body
{
    background: #040E24 !important;
}

hr
{
    border: none !important;
}

.absBotDisclaimer
{
    color: #FF2323 !important;
}

/*Posting*/

table.postForm>tbody>tr>td:first-child 
{
    background-color: #040e24;
    color: #17662a;
    font-weight: bold;
    border: 1px solid #000;
    padding: 0 5px;
    font-size: 10pt;
}

#recaptcha_response_field
{
background-color: #040e24;
color: #17662a;
}

#postForm > tbody > tr:nth-child(4) > td:nth-child(2) > textarea
{
background-color: #040e24;
color: #17662a;
}

#postForm > tbody > tr:nth-child(1) > td:nth-child(2) > input[type="text"]
{
background-color: #040e24;
color: #17662a;
}

/*Catalog*/
#threads
{
color: #17662a;
}

/*Arcieved fix*/
body > div.closed > div
{display: none !important;}

/*FDC-related stuff*/

.de-parea
{
    display: block;
    position: relative !important;
    z-index: 2 !important;
    width: 450px !important;
    left: 10px !important;
}

#de-updater-div
{
    color: #195527 !important;
}

.de-post-counter:after
{
    color: #7C611E !important;
}
/*waifus-related stuff*/

div.post blockquote.postMessage
{
    position: relative !important;
    z-index: 1 !important;
}

div.post div.file .fileThumb img
{
    position: relative !important;
    z-index: 2 !important;
}

div.reply
{
    margin-left: 10px !important;
}

.mFileInfo
{
    position: relative !important;
    z-index: 2 !important;
}
}
