* {
	font-family: 'Open Sans', Verdana, Arial, Helvetica, sans-serif !important;
}

/*            */
/* BACKGROUND */
/*            */

#col_messages,
.p-message_pane .c-message_list.c-virtual_list--scrollbar>.c-scrollbar__hider,
.p-message_pane .c-message_list:not(.c-virtual_list--scrollbar) {
    background: /*[[background]]*/;
}

/*       */
/* LINKS */
/*       */

a, a:link, a:active, a:hover, a:visited {
	color: #2fa9e2;
	text-decoration: none;
}

a:hover {
	text-decoration: underline;
}


/*        */
/* HEADER */
/*        */

.client_container .channel_header {
    border-bottom: 2px solid #E0E0E0;
    padding: 6px 0;
}

.flex_header {
    margin: 7px 0 0;
    height: 34px;
}

.channel_title {
    margin-top: 0;
}

.channel_title_info {
    margin-top: 8px;
}

.c-scrollbar__hider {
    right: 0;
}

.c-scrollbar__track {
    background: #b5bdc3;
    border-radius: 4px;
    top: 4px !important;
    bottom: 4px !important;
}

.c-scrollbar__bar {
    background: #84919a;
}

#client_body:not(.onboarding):before,
.p-message_pane .c-message_list.c-virtual_list--scrollbar>.c-scrollbar__hider:before,
.p-message_pane .c-message_list:not(.c-virtual_list--scrollbar):before {
    display: none;
}

/*         */
/* SIDEBAR */
/*         */

.p-channel_sidebar__static_list > div {
    height: auto !important;
}

.p-channel_sidebar__channel, .p-channel_sidebar__link {
    height: 20px;
    line-height: 20px;
    font-size: 14px;
}

.p-channel_sidebar__section_heading {
    height: 30px;
    padding-top: 4px;
}

.p-channel_sidebar__channel--im .c-presence--active,
.p-channel_sidebar__channel--im-slackbot:before {
    color: inherit;
}

.p-channel_sidebar__channel--im-slackbot:before {
    margin-right: 5px;
}

.p-channel_sidebar__section_heading[data-qa-channel-sidebar-section-heading="starred"] {
    display: none;
}

.p-channel_sidebar__section_heading_label {
    font-weight: bold;
    font-size: 14px;
    padding-left: 16px;
    text-transform: uppercase;
}

.p-channel_sidebar__name.p-channel_sidebar__name--away {
	opacity: 0.35;
}

.p-channel_sidebar__channel--unread,
.p-channel_sidebar__channel--unread .p-channel_sidebar__name,
.p-channel_sidebar__link--unread .p-channel_sidebar__name,
.p-channel_sidebar__link--invites .p-channel_sidebar__name {
    color: /*[[unreadchannel]]*/ !important;
}

.p-channel_sidebar__badge,
.p-channel_sidebar__banner--mentions {
	background: /*[[unreadchannel]]*/ !important;
    color: #000000;
    position: absolute;
    right: 13px;
    padding: 0 8px;
}

div.p-channel_sidebar__section_heading[data-qa-channel-sidebar-section-heading="apps"] {
    display: none;
}

/*          */
/* MESSAGES */
/*          */

.c-virtual_list__item,
.c-virtual_list__sticky_container {
    margin-top: -14px;
}

.c-message_list__unread_divider {
    top: 0;
}

.c-message_list__unread_divider__label {
    background: /*[[unreadchannel]]*/ !important;
    color: #544538;
    font-weight: bold;
    padding: 4px 6px 6px;
    margin-top: -10px;
    font-size: 13px;
}

.c-message_list__unread_divider__separator,
.c-message_list__day_divider__line {
    margin-top: 1px;
    border-color: #98a2aa;
    opacity: 1 !important;
}

.c-message,
.c-message.delete_mode {
    padding-left: 12px;
    padding-top: 5px !important;
    margin-left: 61px;
    background: #ffffff;
    border-radius: 5px;
    max-width: 700px;
    min-width: 450px;
    float: left;
    border-bottom: 2px solid #ced6dd;
    font-size: 13px !important;
    line-height: 18px;
    color: #2c2d30;
    margin-top: 4px;
}

.c-message--light .c-message__content_header {
    margin-top: 12px;
}

.c-message--light .c-message__content {
    margin-left: 0;
    margin-right: 12px;
}

.c-message--light .c-message__gutter {
	left: -56px;
    margin-right: -56px;
    width: auto;
    padding-right: 0;
    position: relative;
}

.c-avatar__image {
    border-radius: 50%;
}

.c-message_list__day_divider {
    padding: 18px 0 14px 0;
}

.c-message_list__day_divider__label {
    top: 6px;
}

.c-message_list__day_divider__label__pill {
    background: #98a2aa;
    color: #ffffff;
    font-weight: normal;
    font-size: 13px;
    top: 14px;
}

.p-degraded_list__loading + .c-virtual_list__sticky_container .c-message_list__day_divider__label__pill {
    top: 8px;
}

.c-message__actions {
    right: 0;
    top: 0 !important;
    border: none;
    background: none;
    box-shadow: none !important;
}

.c-message_actions__button {
    border: none;
}

[lang] ts-message {
    font-size: 13px;
}

#convo_container ts-conversation ts-message.selected .message_content .message_body {
    margin-left: 46px;
}

#notification_bar
{
    height: 14px;
    bottom: 0;
}

#notification_bar.wide #typing_text
{
    padding-left: 40px;
}

.p-notification_bar {
    align-items: baseline;
}

#typing_text
{
    line-height: 16px;
}

#special_formatting_text
{
    line-height: 14px;
}

#footer
{
	background: 0;
}

#client-ui:not(.flex_pane_showing) .client_container #footer
{
    margin-right: 0;
}

#msg_form {
    margin: 0 14px;
}

#primary_file_button {
    width: 44px;
    height: calc(100% - 14px);
    display: flex;
    align-items: center;
    justify-content: center;
}

#primary_file_button .ts_icon {
    position: static;
}
  
#message_edit_form .emo_menu,
#msg_form .emo_menu,
.current_status_container .emo_menu,
.current_status_input_container .emo_menu,
.inline_message_input_container .emo_menu,
.share_channel_modal_contents .emo_menu,
#message_edit_form .msg_mentions_button,
#msg_form .msg_mentions_button,
.current_status_container .msg_mentions_button,
.current_status_input_container .msg_mentions_button,
.inline_message_input_container .msg_mentions_button,
.share_channel_modal_contents .msg_mentions_button {
    bottom: 15px;
}

.star:before {
    font-size: 16px !important;
}

#app_messages {
	display: none;
}
}
