/*Header color*/
h1, h2, h3, h4, h5 {
    color: /*[[header color]]*/ !important;
    text-shadow: /*[[header color]]*/ 0px 0px 10px !important;
}

/*PP color*/
.pp-display b{
    color: /*[[pp color]]*/ !important;
    text-shadow: /*[[pp color]]*/ 0px 0px 10px !important;
}

/*fix for white box on homepage*/
.blurb {
    background-color: rgba(50,50,50,0.5);
    color: rgb(200,200,200);
}
.blurb-title {
    color: #15DFDF!important;
}

/*making login popup readable*/
.login-dropdown {
    background-color: rgb(50,50,50);
}

/*making register page readable*/
.reg-form div {
    background: rgba(0,0,0,0.3);
}
.reg-form .feedback{
    background-color: rgba(0,0,0,0);
}

/*google search bar*/
.content-infoline .inline-form div input{
    color: #ddd !important;
    background: none !important;
    border: 1px solid #15BFDF!important;
    background-color: rgba(0,0,0,0.3)!important;
    width: 87px;
    height: 15px;
    padding: 0px !important;
}
  
/*fixing news articles like /news/122549062138 */
.blog-post {
    background: rgba(0,0,0,0.3) !important;
    border-radius: 3px;
}

/*graph popup*/
.nvtooltip, .xy-tooltip {
    background-color: rgba(0,0,0,0.3);
}
.nvtooltip, .xy-tooltip h3{
    background-color: rgba(0,0,0,0.3);
    color: #15DFDF !important;
    text-shadow: #15DFDF;
}
  
/*google search improvements*/
.gsc-control-cse, .gsc-control-cse-en, .gsc-webResult .gsc-result{
    background: rgba(0,0,0,0.3);
}
.gs-webResult .gs-snippet, .gs-imageResult .gs-snippet, .gs-fileFormatType{
    color: #3843F6;
}
.gsc-results .gsc-cursor-box .gsc-cursor-page{
    background-color: transparent;
}
  
/*wiki improvements*/
.wikitable, .wikitable th, .toccolours{
    background: rgba(0,0,0,0.3) !important;
}
.toccolours tr td{
    background: transparent !important;
}
code {
    background: rgba(32,32,32, 1);
    padding: 3px 7px;
}

/*Autocomplete when searching*/
.autocomplete {
    color: rgb(200,200,200);
    background: rgb(20,20,20);
}
.stream {
    background: rgba(255, 224, 255, 0.5) !important;
}
.stream>.title {
    color: inherit !important;
}
}

/*:)*/
@-moz-document url-prefix("https://osu.ppy.sh/pages/include/profile-leader.php") {
td:first-child {
    width: 85%;
}
tr td:last-child {
    text-align: right;
    width: auto;
}
body{
    background: rgb(100,100,100);
    margin: 10%;
    margin-top: 30px;
}
.prof-beatmap{
    width: 80%;
    text-align: center;
    margin: 0px;
}
}
