css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.pixiv.net/novel/") {
  .novel-contents .imgbox .banner span {
    background-color:/*[[シリーズの帯の背景色]]*/ !important;}
  .novel-contents .imgbox .banner:before {
    border-bottom-color:/*[[シリーズの帯の影の色]]*/ !important;}
  .novel-contents .imgbox .banner:after {
    border-top-color:/*[[シリーズの帯の影の色]]*/ !important;}
}
