css:
@-moz-document domain("empeopled.com") {
@media (min-width: 768px) {
  .govern > .container > div, .post, .future {
      padding: 5px 0px;
  }

  /**
   * These contain a lot of whitespace which prevent them being collapsed properly
   */
  .row.stickie .actions .contextual {
      display: none;
  }

  /**
   * Slim down headers
   */
  .future .future-info h3,
  h3.post-title,
  .govern > .container h3 {
      font-size: 120%;
  }

  /**
   * Slim down future text on list pages and all text on govern page
   */
  .future .future-info p, .govern > .container p {
      font-size: 100%;
  }

  /**
   * Slim down regular posts
   */
  .post .comments-count-wrap {
      padding-top: 20px;
  }

  .post .media-image-link, .post .media-image-wrap {
      height: 55px;
      width: 55px;
      background-size: cover;
  }

  .post .post-info .contextualinfo {
      display: inline-block;
      vertical-align: top;
      margin: 1px 0 5px;
  }

  :not(.discussion-content) > .post .actions {
      display: inline-block;
      margin: 5px;
      float: right;
      text-align: right;
  }

  .post .actions :empty:not(.fa) {
      display: none;
  }

  .post .stickie-dtf {
      bottom: initial;
      top: 25px;
  }

  .post .new-comment {
      position: absolute;
      top: 0;
  }

  /**
   * Slim down new topic voting list
   */

  .choice {
      padding: 10px 0;
  }

  .choice:first-child {
      margin-top: 15px;
  }

  .choice h3 {
      font-size: 120%;
  }

  .choice .choice-info {
      padding: 5px 30px;
  }

  .choice .choice-info p {
      font-size: 1rem;
      margin: 0px;
  }

  .choice .votes i {
      font-size: 35px;
  }

  .choice .votes .vote-sum {
      font-size: 16px;
  }
}
@media (min-width: 992px) {
    .post-info .col-md-2 {
        width: 10%;
    }

    .choice .left-info {
        padding-top: 10px;
    }
}

/*[[color_mode]]*/
}
