@-moz-document domain("twitter.com") {

.AdaptiveMedia, .OldMedia {
    max-height: 100% !important;
}

.AdaptiveMedia-doublePhoto, .AdaptiveMedia-halfWidthPhoto, .AdaptiveMedia-thirdHeightPhoto, .AdaptiveMedia-triplePhoto, .AdaptiveMedia-quadPhoto, .OldMedia-doublePhoto, .OldMedia-triplePhoto, .OldMedia-quadPhoto {
    height: 100% !important;
}

.AdaptiveMedia-halfHeightPhotoContainer, .AdaptiveMedia-quarterPhotoContainer, .AdaptiveMedia-thirdHeightPhotoContainer, .AdaptiveMedia-threeQuartersWidthPhoto, .OldMedia-quarterPhotoContainer {
    width: 100% !important;
}

.AdaptiveMedia-halfPhoto, .AdaptiveMedia-halfHeightPhoto, .AdaptiveMedia-halfWidthPhoto, .AdaptiveMedia-quarterPhoto, .AdaptiveMedia-twoThirdsWidthPhoto, .OldMedia-halfPhoto, .OldMedia-quarterPhoto {
    position: unset;
    overflow: unset;
    width: 100% !important;
}

.AdaptiveMedia img, .OldMedia img {
    position: unset;
    top: 0 !important;
    height: 100%;
    width: 100%;
}

}
