css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(nulled.io) { 
/* Color Changer */
  .maintitle, ul#menutabs a.selected {
background: /*[[color]]*/ !important;
}

#secondary_navigation {
    border-top: 5px solid /*[[color]]*/;
    border-left: 5px solid /*[[color]]*/;
    border-right: 5px solid /*[[color]]*/;
}

#branding {
    border-bottom: 5px solid /*[[color]]*/;
}

#user_bar {
    border-top: 5px solid /*[[color]]*/;
}

#stats_div {
    border-bottom: 5px solid /*[[color]]*/;
    border-left: 5px solid /*[[color]]*/;
    border-right: 5px solid /*[[color]]*/;
}

#footer_utilities {
    border-bottom: 2px solid /*[[color]]*/;
    border-top: 4px solid /*[[color]]*/
}

.cfunread i { 
color: /*[[color]]*/;
}

.ipsHeaderMenu {
    border: 2px solid /*[[color]]*/;
}

.topic_buttons li a:hover, .ipsButton:hover {
    background: /*[[color]]*/ none repeat scroll 0% 0%;
}

.ipsFilterbar li.active a, .ipsFilterbar.bar.altbar li.active a {
    background: /*[[color]]*/ none repeat scroll 0% 0% !important;
}

.pagination .pages li.active {
    background: /*[[color]]*/ none repeat scroll 0% 0%;
}

.pagination .page a:hover,
    .pagination .back a:hover,
    .pagination .forward a:hover {
        background: /*[[color]]*/;
  }
.maintitle {
    background: /*[[color]]*/ none repeat scroll 0% 0%;
}
.header{
    background:/*[[color]]*/;
}
#more_topics {
    background: /*[[color]]*/;
}
blockquote.ipsBlockquote {
 border-color: rgba(194,191,191,.2) rgba(194,191,191,.2) rgba(194,191,191,.2) /*[[color]]*/;
}
p.citation {
    border-color: rgba(194, 191, 191, 0.2) rgba(194, 191, 191, 0.2) rgba(194, 191, 191, 0.2) /*[[color]]*/;
}
.edit {
    background: /*[[color]]*/ url("https://nf-14a3.kxcdn.com/public/style_images/images/comment_edit.png") no-repeat scroll 6px 10px;
}
.rep_down {
    background: #e74c3c none repeat scroll 0% 0%;
    border: 1px solid #e74c3c;
}
.rep_up {
    background: #27ae60 none repeat scroll 0% 0%;
    border: 1px solid #27ae60;
}
.rep_positive {
    background: #27ae60 none repeat scroll 0% 0% !important;
}
.rep_negative {
    background: #e74c3c none repeat scroll 0% 0% !important;
}
ul.post_controls a:hover { 
    background: /*[[color]]*/; 
}
    ::-webkit-scrollbar-thumb {
  background: /*[[color]]*/;
}
#search .submit_input {
background: /*[[color]]*/ url('https://nf-14a3.kxcdn.com/public/style_images/images/search_icon.png') no-repeat 50%;
}
}
