css:
@-moz-document domain("roblox.com") {
#header {
        background-color: /*[[customcolor]]*/;
    }
    
.navigation .notification-icon {
        background: /*[[customcolor]]*/;
    }

.forum-table-header, body .rbx-highlight, body .forum-table-header th, body .forum-table-footer, #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th {
        background: /*[[customcolor]]*/ !important;
    }

.rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs, .rbx-btn-secondary-xs.btn-more, #AjaxCommentsContainer > div > div.comment-form > form > button {
  		background-color: /*[[customcolor]]*/ !important;
   		border-color: /*[[customcolor]]*/;
    }

.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs:hover, .rbx-btn-secondary-xs.btn-more:hover, #AjaxCommentsContainer > div > div.comment-form > form > button:hover {
        background: /*[[secondarycolor]]*/ !important;
        border-color: /*[[secondarycolor]]*/;
    }

.btn-medium btn-primary tooltip{
    background: #5A5A5A;
}

#TopAbpContainer {
	display: none;
}

.ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer {
  		display: none;
  	}

a.ad img {
    height: 0px;
    width: 0px;
}

#MasterContainer, .nav-content-inner, .container-main, .gutter-ads {
        background: url(/*[[bgimage]]*/) top center repeat-x /*[[bgcol]]*/;
    }
    
#navbar-search-input {
		border: 1px solid /*[[customcolor]]*/;
    }

body {
  	font-family: Segoe UI;
	}
}
