css:
@-moz-document domain("www.v3rmillion.net") {
ul.navigation li:first-child {
    border-bottom: 40px solid /*[[primary]]*/;
}
  
.thead, #content > ul, ul.navigation li:first-child {
    background: /*[[primary]]*/;
}
  
  
 input.button{
   background: /*[[primary]]*/;
 }
.pagination .pagination_current{
    background: /*[[primary]]*/;
}
  
.tfoot{
    background: /*[[primary]]*/;
}
  
#bridge{
    border-bottom: 4px solid /*[[primary]]*/;
} 



ul.menu li a.active, ul.menu li a:visited {
    background: /*[[tert]]*/;
}
  
#header{
  border-top-style: none;
  border-right-style: none;
  border-bottom-style: none;
  border-left-style: none;
  border: none;
}
  
#header > div > a > img{
    content:url(/*[[headerimage]]*/);
}
  
a:link, a:visited, ul.menu li a:link, ul.menu li a:visited{
    color: /*[[secondary]]*/;
}
  
ul.menu li a.active{
    color: /*[[secondary]]*/;
}
  
.star_rating, .star_rating li a:hover, .star_rating .current_rating{
    content: none;
width: 0px;
height: 0px;
}

a.large_button {
        vertical-align: middle;
        margin-top: -2px;
        margin-right: 5px;
        padding: 3px;
}
  
body{
    background: /*[[tert]]*/
}
  
.messageEditor, .messageEditor .toolbar_normal{
    background: /*[[primary]]*/;
    border: 1px solid /*[[primary]]*/;
}

textarea:focus{
  border: 1px solid /*[[primary]]*/;
  box-shadow: 0px 0px 3px /*[[primary]]*/;
  -moz-box-shadow: 0px 0px 3px /*[[primary]]*/;
  -webkit-box-shadow: 0px 0px 3px /*[[primary]]*/;
}

tbody > tr:nth-child(2) > td > table > tbody > tr > td.post_author > strong > span > a{
color: /*[[namecolors]]*/ !important;
}
  
.myalerts_popup .popupTitle{
background: /*[[secondary]]*/ !important;
}
  
.myalerts_popup .popupFooter{
background: /*[[secondary]]*/ !important;
}
  
.myalerts_popup_wrapper .myalerts_popup{
color: /*[[secondary]]*/ !important;
}
  
.myalerts_popup li{
background: /*[[secondary]]*/ !important;
border-bottom: transparent;
}
  
.myalerts_popup li .alertContent{
background: transparent !important;
}
  
#panel > div > div > a:nth-child(1){
  content:"Welcome! Stylish edit created by Isla";
}
  
.post_buttons a, a.btn_report{
  color: /*[[secondary]]*/ !important;
background-color: /*[[tert]]*/ !important;
}
  
#container{
  color: /*[[secondary]]*/ !important;
}
  
.pagination a{
  background-color: /*[[tert]]*/ !important;
}
  
a.large_button{
  background-color: /*[[tert]]*/ !important;
}
  
#container > table:nth-child(9) > tbody > tr:nth-child(2){
   color: /*[[tert]]*/ !important;
}
  
.tcat a:link, .tcat a:visited, #content > ul > li.nav_active > span{
    color: /*[[tert]]*/ !important;
}
  
.tcat{
  color: /*[[secondary]]*/ !important;
}
  
#vermcpright > span {
  visibility: hidden;
}
  
#vermcpright > span:after {
  content: "Powered By electricity MyBB Â© CSS Userstyle by TheTrueRias";
  visibility: visible;
}
  
#panel .links a{
    color: /*[[secondary]]*/ !important;
  	background-color: /*[[tert]]*/ !important;
}
  
#container > div:nth-child(4) > strong {
	color:transparent !important;
}
#aplace > a > img, #show_id{
visibility: hidden;
content:none;
}
  
  
}
