css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

#urlbar {
  opacity: 0.7 !important;
  background: /*[[url_bgcolor]]*/ !important;
  color: /*[[url_color]]*/  !important;
}

#urlbar:hover {
  opacity: 0.8 !important;
  background: /*[[url_bgcolor]]*/ !important;
  color: /*[[url_color]]*/  !important;
}

#urlbar[focused="true"] {
  opacity: 1.0 !important;
  background: /*[[url_bgcolor]]*/ !important;
  color: /*[[url_color]]*/  !important;
}

