  opacity: 0.7 !important;
  background: /*[[search_bgcolor]]*/ !important;
  color: /*[[search_color]]*/  !important;
}

#searchbar .searchbar-textbox:hover {
  opacity: 0.8 !important;
  background: /*[[search_bgcolor]]*/ !important;
  color: /*[[search_color]]*/  !important;
}

#searchbar .searchbar-textbox[focused="true"] {
  opacity: 1.0 !important;
  background: /*[[search_bgcolor]]*/ !important;
  color: /*[[search_color]]*/  !important;
}
