css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kaniwani.com") {
  
/*----------------------------------------------------------------------------------------------------*/
/*---------------------------------------------General Site-------------------------------------------*/
/*----------------------------------------------------------------------------------------------------*/

html, body, .row, .container-fluid, header.site-header, footer.section.site-footer, .site .site-content .announcements-section {
  background-color: /*[[background.color]]*/ !important;
}

input, textarea {
  background-color: #252525 !important;
}

.name {
  color: #606060 !important;
}
section.user-section {
  background-color: #555 !important;
  background-blend-mode: multiply !important;
}

.reviews button, .reviews a {
  background-color: #616161 !important;
}

section.user-section {
  background-image: url("/*[[background.image]]*/") /*[[background.custom]]*/ !important;
}

/*--------------------------------------------------------------------------------------------------------*/
/*-------------------------------------------------Reviews------------------------------------------------*/
/*--------------------------------------------------------------------------------------------------------*/

.review-quiz .upper {
  background-image: linear-gradient(to bottom, #90B, #9000B0) !important;
}

input#userAnswer {
  background-color: #252525 !important;
  color: /*[[input.standard]]*/ !important;
}
#answerPanel.answerpanel.-marked.-correct input#userAnswer {
  color: /*[[input.correct]]*/ !important;
}
#answerPanel.answerpanel.-marked.-incorrect input#userAnswer {
  color: /*[[input.incorrect]]*/ !important;
}

button.button.revealToggle, .sc-1y6l0g0-1 {
  background-color: #111 !important;
}
p.text.-kanji, p.text.-kana {
  background-color: #252525 !important;
  color: #DDD !important;
}

div.review-extra div.row {
  background-color: #404040 !important;
}
.review-bg div {
  background-color: #333 !important;
  background-blend-mode: multiply !important;
}
  

}
