css:
@-moz-document domain("minecraftservers.org") {

.online {background: /*[[Online]]*/ !important}
.offline {background: /*[[Offline]]*/ !important}

/*Removes Ads*/
.second-banner, .google-ad {
	display: none !important;
}

/*Navbar*/
.nav-banner {
    background: /*[[Nav]]*/ !important;
    opacity: .8 !important;
    position: fixed !important;
    width: 100% !important;
    z-index: 100 !important;
    
}

.brand-banner {padding-top: 50px !important;}

/*Body Background*/
#main, #header, .brand-banner, .second-banner, .page-server, body {
	background: /*[[Page Background]]*/ !important;
}

/*Blocks*/
#footer, .serverlist thead tr th, .server-ip, .copy-action, .button, .section-head, .tags a, .vote, .version a {
	background: /*[[Blocks]]*/ !important;
    color: #fff !important;
}

/*Text Color*/
h1, h2, h3, h4, h5, h6, p, a, .count {
	color: /*[[Text Color]]*/ !important;
}

/*Copy Button*/
.copy-action:hover, .zeroclipboard-is-hover {
	background: /*[[Copy Button Hover]]*/ !important;
}

/*Bottom Page Navigation*/
.current-page, .pagination li:not(.current-page):hover {
	background: /*[[PageNav Button Hover]]*/ !important;
}

/*Bottom Page Navigation*/
.pagination li:not(.current-page) {
	background: /*[[PageNav Button]]*/ !important;
}

.vote:hover {
	background: #1c1c1c !important;
}

.tab-content, .server-info td {
	background: #333 !important;
    border: 1px solid #242424 !important;
    color: #fafafa !important;
}

.section-head {
	border: 1px solid #242424 !important;
}

.tab-link-active, .tab-links li:hover {
	border-bottom: 6px solid #1c1c1c !important;
}

#field-container, .content-area, .flash {
	background: #484848 !important;
    border: 1px solid #1c1c1c !important;
    color: #fafafa !important;
}

.sponsored-servers {padding-bottom: 216px !important}

#sponsored {
	padding-bottom: 40px;
}}
