css:
@-moz-document domain("discordapp.com") {
/* server buttons */
.guilds-wrapper { width: 55px; }
.guilds-wrapper .guilds { padding: 9px 7px; width: 65px; }
.guilds-wrapper .guilds .guild { width: 40px; height: 40px; }
.guilds-wrapper .guilds .guild .avatar-small { width: 40px; height: 40px; background-size: 40px 40px; }
.guilds-wrapper .guilds .guild .guild-inner a { width: 40px; height: 40px; }
.guilds-wrapper .guilds .guild.unread:before { left: -14px; }
.guilds-wrapper .guilds .friends-icon { width: 41px; height: 43px; }
.guilds-wrapper .guilds .friends-online { width: 42px; }
.guilds-add { line-height: 0%; }

/* channels sidebar */
.guild-channels header { margin: 0; padding-left: 4px; padding-top: 5px; }
.guild-channels .channel.channel-text a { padding: 4px 6px 4px 7px; }
.guild-channels .channel.channel-text.unread:before { top: 7px !important; }
.guild-channels .channel.channel-text.selected.unread:before { top: 0 !important; }
.guild-channels .channel.channel-voice { padding: 4px 6px 4px 7px; }
.channels-wrap { width: 225px; }
.account { height: 50px; }
.links { font-size: 9px; }

/* chat */
.chat > .title { height: 42px; padding: 0 10px; }
.message-group { padding: 10px 0; margin-left: 10px; }
.message-group h2 span { float: right; }
.message-group .avatar-large { margin-right: 7px; }
.theme-dark .chat>.content, .theme-dark .friends-table, .theme-dark .messages-wrapper { background-color: #222425; }
.theme-dark .chat form .typing { background-color: transparent; }
.theme-dark .chat>.content .messages .divider span { background-color: transparent; }

/* message input */
.chat form { margin: 0 20px 0 10px; }
.channel-textarea { margin: 7px 0 30px; }
.channel-textarea-inner { padding: 7px; border: 1px solid #dadddf; }
.channel-textarea-upload { width: 40px; margin: -7px 7px -7px -7px; }
.theme-dark .channel-textarea-upload { background-size: 20px 20px; }
.theme-dark .channel-textarea-upload:hover { background-size: 20px 20px; }

/* chatters sidebar */
.channel-members-wrap { min-width: 130px; }
.channel-members { padding: 7px 0; }
.channel-members .member { padding: 5px 5px 5px 7px; }
}
