css:
@-moz-document domain("play.spotify.com") {
* { -webkit-box-sizing: content-box; -moz-box-sizing: content-box; /* box-sizing: content-box; */ }

#suggest-area { visibility: hidden }
#main { display: none; }
#main-nav { visibility: hidden }
.sp-image-placeholder{ display: none; }

#now-playing { width: 100%; height: 100%; position: static; }
#now-playing-widgets { display: none; }
#app-player { height: 100%; }

html { height: 100%; }
body { height: 100%; }
#wrap { height: 85%; }
#art-area { height: 80%; }
#cover-art { width: 100%; height: 100%; }
#cover-art a { width: 100% !important; height: 100% !important; }
#cover-art a .sp-image-wrapper { width: 100%; height: 100%; }
#cover-art a .sp-image-wrapper .sp-image-img { background-size: contain !important; background-repeat: no-repeat !important; }
}
