css:
@-moz-document regexp(".*amara.org.*subtitles.editor.*") {

.subtitle-text, div.subtitles ul.subtitle-list li textarea:focus, div.subtitles ul.subtitle-list li.empty, 
div.subtitles ul.subtitle-list li.empty textarea,div.subtitles ul.subtitle-list li.empty span {
	font-family: '/*[[font-family]]*/';    
	font-size: /*[[font-size]]*/px;
	color: #D0D0D0;
	background-color: #303030;
	font-weight: lighter;
}

.help-panel, .video, .workflow, .edit-area, h1 {
	color: #D0D0D0;
	background-color: #303030;
}

div.subtitles ul.subtitle-list li span.timing {
	color: #909090;
}

div.notes .new-note textarea {
	color: #D0D0D0;
	background-color: #404040;
}

}
