css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('trello.com')
{
    #board{
        display:block !important;
        text-align:left;    
        position:relative !important;
        white-space:initial;
        padding-bottom: 24px;
    }
    .board-canvas{
        height:auto !important
    }
    .body-board-view{
        overflow-y:visible
    }
    .js-list,
    .js-add-list {
        display:inline-block !important;
        vertical-align:top;
        margin:0 0 10px 10px;
    }
    .u-gutter{
        margin-left:0;
    }
}
