css:
/* General CSS */
@-moz-document regexp('http://brantsteele\.net/hungergames/(bloodbath|night|day|arena|placement|summary|statistics|reaping|fallen|feast|AdjustSize|edit).*') {
  html {
    height: 100%;
  }
  
  body {
    /* General text font and size */
  	font-family: "/*[[textfont]]*/"!important;
  	/*[[fontsize]]*/
    /* General background image */
  	background-image: url(/*[[backgroundimage]]*/)!important;
  	background-position: calc(212px + /*[[xoffset]]*/) calc(46px + /*[[yoffset]]*/)!important;
  }
  
  table {
    background-color: rgba(0, 0, 0, 0)!important;
    margin-bottom: -18px!important;
  }
  
  div#content{
    /* Text shadow and color */
    /*[[textshadow]]*/
    color: /*[[textcolor]]*/!important;
  }
  
  #content img{
    /* Image dimensions, shadow and rounded corners */
    /*[[imagesize]]*/
    /*[[boxshadow]]*/
    border-radius: /*[[roundedcorners]]*/!important;
  }
  
  table.two{
    table-layout: auto!important;
  }
  
  font {
    color: /*[[fontcolor]]*/!important;
  }
  
  a {
    color: #DD4443!important;
  }
  
  #title {
    /* Title color */
    color: /*[[titlecolor]]*/!important;
    background: rgba(23,23,23,0.7) none repeat scroll 0% 0%!important;
    /*[[boxshadow]]*/
    font-family: "/*[[titlefont]]*/"!important;
    margin-left: 61px!important;
  }
  
   ul.drop a, #sideLeft, #sideLeft2, #sideLeft3 {
    color: #FFF!important;
    font-family: Verdana!important;
    font-size: 80%!important;
    text-decoration: none!important;
  }
  
  a.navbar2, a.navbar3, a.navbar2A{
    color: #FFF!important;
  }
  
}


/* Fallen Tributes CSS */
@-moz-document regexp('http://brantsteele\.net/hungergames/(fallen).*'){
  body {
    /* Fallen Tributes background image*/
  	background-image: url(/*[[fallenimage]]*/)!important;
  }
  
  table {
  	margin-bottom: -2px!important;
  }
  
  td {
    font-family: "/*[[textfont]]*/"!important;
  }
  
}


/* The Reaping CSS */
@-moz-document regexp('http://brantsteele\.net/hungergames/(reaping).*'){
  FONT {
    color: #74f424!important;
    /* Reaping text font and size */
    font-family: "/*[[textfont]]*/"!important;
    /*[[fontsize]]*/
  }
  
  tr {
    font-family: "/*[[textfont]]*/"!important;
  }
  
  td.district {
    /* District font, color, size and shadow */
    font-family: "/*[[districtfont]]*/"!important;
    color: /*[[districtcolor]]*/!important;
    /*[[districtsize]]*/
    /*[[districtshadow]]*/
  }
  
}


/* Placements CSS */
@-moz-document regexp('http://brantsteele\.net/hungergames/(placements).*'){
  #content img {
    margin-top: 10px!important;
  }
  
}


/* Arena CSS */
@-moz-document regexp('http://brantsteele\.net/hungergames/(arena).*'){
  body {
    /* Arena background image */
    background-image: url(/*[[arenaimage]]*/)!important;
  }
  
}


/* Feast CSS */
@-moz-document regexp('http://brantsteele\.net/hungergames/(feast).*'){
  body {
    /* Feast background image */
    background-image: url(/*[[feastimage]]*/)!important;
  }
  
}


/* Night CSS */
@-moz-document regexp('http://brantsteele\.net/hungergames/(night).*'){
  body {
    /* Night background image */
    background-image: url(/*[[nightimage]]*/)!important;
  }
  
}


/* Winner CSS */
@-moz-document regexp('http://brantsteele\.net/hungergames/(winner).*'){
  a {
    color: #DD4443!important;
  }
  
  #content {
    font-family: "/*[[textfont]]*/"!important;
  } 

  #content img{
    height: 100px!important;
    width: 100px!important;
    margin-top: 10px!important;
    margin-bottom: 10px!important;
    border-radius: /*[[roundedcorners]]*/!important;
  }
  
  ul.drop a, #sideLeft, #sideLeft2, #sideLeft3  {
    color: #FFF!important;
    font-family: Verdana!important;
    font-size: 80%!important;
    text-decoration: none!important;
  }
  
  table {
    background-color: rgba(0, 0, 0, 0)!important;
    margin-bottom: -20px!important;
    margin-top: -10px!important;
  }
  
  #title {
   margin-left: 61px!important;
   color: /*[[titlecolor]]*/!important;
  }

  a.navbar2, a.navbar3, a.navbar2A{
    color: #FFF!important;
  }
  
}


