css:
@-moz-document url("http://www.dmm.com/netgame/social/-/gadgets/=/app_id=265103/") {
body {
    margin: 0;
    min-width: 0;
    background-color: /*[[bgcolor]]*/;
}
body * {
    display: none;
}
#w, #main-ntg, #page, #area-game, #game_frame {
    display: block;
}
#main-ntg {
    /*position: relative;*/
    margin: 0;
    padding: 0;
    width: 100%;
    left: 0;
    /*[[position_y]]*/
}
@media all and (min-aspect-ratio: 10/6) {
    #main-ntg {
        width: -webkit-calc(100vh / 0.6);
        width:    -moz-calc(100vh / 0.6);
        width:         calc(100vh / 0.6);
        /*[[position_x]]*/
        top: 0;
    }
}
#main-ntg:after {
    /* 画面比率は1100:650なので正確には 59.0909...% */
    padding-top: 60%;
    display: block;
    content: "";
}
#area-game {
    position: absolute;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
}
#game_frame {
    width: 100% !important;
    height: 100% !important;
}
}

@-moz-document regexp("^http:\\/\\/osapi\\.dmm\\.com\\/gadgets\\/ifr\\?&?(\\w+=[^&]*&)*aid=265103(&\\w+=[^&]*)*#rpctoken=\\d+$") {
body > *:not(#contents) {
    display: none;
}
#flash > img, /* メンテナンス中 */
#flash_object {
    position: absolute;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    width: 100%;
    height: 100%;
}
}
