css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("share.dmhy.org") {
  
  /*Main Page*/
  
  .container .bg {
    margin: 0px !important;
    min-width: 960px !important;
  }
  div.header { background: #28477D !important; }
  .headerright .links { background: transparent !important; }
  #mini_jmd { width: auto !important; }
  table.tablesorter { border-spacing: 0px !important; }
/*[[jmd]]*/
  
  .quick_search {
    width: auto !important;
    margin: 0px !important;
    padding-bottom: 8px !important;
    border-bottom-width: 0px !important;
  }
  .quick_input { width: 800px !important; }
  .formButton { padding: 0px 5px !important; }
  
  .announce  { display: none !important; }
  div[style$="padding-bottom: 3px;"] {
    width: auto !important;
    padding: 6px 8px 7px !important;
    position: absolute !important;
    top: 22px !important;
    right: 0px !important;
  }
  a[href="/topics/add"] { color: #FFF !important; }
  a[href="/topics/add"]:after { content: "發佈BT資源" !important; }
  img[src="/images/btn_postbt.gif"] { display:none !important; }
  
  .table { padding: 0px !important; }
  .table.clear {
    border-top-width: 0px !important;
    padding-top: 0px !important;
    background: #CDF none repeat scroll 0% 0% !important;
  }
  .nav_title { padding: 2px 6px !important; }
  
  /*end*/
  
  /*Topic Page*/
  
  .topics_bk {
    border-top-width: 0px !important;
    padding-top: 0px !important;
    border-radius: 0px !important;
  }
  .topic-main { position: relative !important; }
  .topic-main .resource-detail {
    position: absolute !important;
    border: 0px !important;
    margin: 8px !important;
    top: 32px !important;
    left: 180px !important;
    right: 0px !important;
    max-height: 156px !important;
    overflow: auto !important;
  }
  .topic-title { height:204px !important; }
  .resource-info li:nth-last-child(4):after {
    font-size: 12px !important;
    content: "右邊的檔案列表上方" !important;
  }
  a[href="#description-end"] { display: none !important; }
  li.share:before {
    font-size: 12px !important;
    content: "分享資源: " !important;
  }
  li.share { font-size:0px !important; }
  
  .info.resource-info {
    margin-top: 0px !important;
    float: left !important;
  }
  
  .ui-tabs-panel {
    margin: 0px !important;
  }
  .ui-tabs-panel p {
    margin-bottom: 2px !important;
  }
  .file_list:before {
    font: 13px arial,tahoma,verdana,sans-serif !important;
    font-weight: bold !important;
    content: "檔案列表: " !important;
  }
  .file_list {
    border: 0px !important;
    width: 100% !important;
    padding: 0px !important;
    background: #eef !important;
  }
  .file_list ul {
    border: 1px solid #247 !important;
  }
  
  .ui-tabs-nav.ui-helper-reset { display: none !important; }
  .ui-tabs .ui-tabs-panel { padding: 0px !important; }
  
  div[class="topic-nfo box ui-corner-all"] ~ br  { display: none !important; }
  #before-comment { display: none !important; }
  
  /*end*/
  
  /*Advertisement*/
  div[id$="_ad"] { display: none !important; }
  div[id$="_adv"] { display: none !important; }
  div[class="box ui-corner-all"] { display: none !important; }
  div[class="ypa-popup-overlay"] { display: none !important; }
  div[class^="kiwi-ad"] { display: none !important; }
  iframe[id="id01_370164"] { display: none !important; }
  
  /*end*/
  
}
