css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.bunsenlabs.org") {
  .pun *
  {
    font-family: "/*[[mainfont]]*/" ! important;
    font-size: /*[[mainfontsize]]*/ ! important;
  }
  .pun textarea
  {
    font-family: "/*[[textareafont]]*/" ! important;
    font-size: /*[[textareafontsize]]*/ ! important;
  }
  .pun ul.crumbs *
  {
    font-family: "/*[[crumbsfont]]*/" ! important;
  }
  .pun ul.crumbs
  {
    font-size: /*[[crumbsfontsize]]*/ ! important;
  }
  .pun code
  {
    font-family: "/*[[codefont]]*/" ! important;
    font-size: /*[[codefontsize]]*/ ! important;
  }
}
