css:
@-moz-document url-prefix("http://e-shuushuu.net/") {
/*
E-shuushuu Customizable CSS
rev20191011-2338*/
html
{
background: /*[[bg-opacity]]*/ /*[[bgc]]*/ url('/*[[bg]]*/') !important;
/*[[bgr]]*/
background-attachment: fixed!important;
background-position: center!important;
}


/*[[tranf]]*/ /*[[shuu-bnr]]*/ /*[[app]]*/ /*[[effect]]*/

#banner a
{
position:absolute;
/*[[banner-img]]*/
}


#banner:after {
content : "";
display: block;
position: absolute;
top: 0;
left: 0;
background: url('/*[[bnr-img]]*/')!important;
background-repeat:no-repeat;
background-size:cover;
background-attachment: fixed;
background-position: center;
width: 100%;
height: 100%;
opacity : /*[[bn-opacity]]*/ ;
z-index: 0;
}
}

@-moz-document url-prefix("http://e-shuushuu.net/") {
/*Elements Shadow*/
#donations .graph,
select
{
box-shadow: 1px 1px 4px /*[[elmsh]]*/
}

#head,
#sorting_controls,
#footer,
.thumb img,
.post .avatar img,
.display,
.pagination .next,
.pagination .prev,
.pagination .first,
.pagination li,
input.button,
div#app img,
div#content p img,
div.search_suggest_div,
div.search_suggest_div ul,
div.tooltip,
form.norm,
table,
input.text,
textarea,
.notice
{
box-shadow: /*[[elmsht]]*/ /*[[elmsh]]*/
}

/*Border Color*/
header,
#head,
#sorting_controls,
#footer,
.display,
.notice,
form.norm,
input.text,
div.search_suggest_div ul
{
border: /*[[1px]]*/ /*[[bds]]*//*[[border]]*/!important;
border-bottom: /*[[1px]]*/ /*[[bds]]*//*[[border]]*/!important;
}

#top_info, #banner
{
border-bottom: /*[[1px]]*/ /*[[bds]]*//*[[border]]*/!important;
}

#donations .graph,
select,
textarea,
input.button,
fieldset,
div.noarticletext,
table.narrow,
table,
.thumbview .display,
.pagination .next,
.pagination .first,
.pagination .prev,
.post blockquote,
.post .spoiler,
.pagination li,
form.norm input
{
border: /*[[1px]]*/ /*[[bds]]*//*[[border]]*/!important;
}

.post_foot
{
border-top: /*[[1px]]*/ /*[[bds]]*//*[[border]]*/!important;
}
.post .meta
{
border-top: /*[[1px]]*/ dashed /*[[border]]*/!important;
}
.post .avatar
{
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
background: transparent;
}
.spoiler
{
background: rgba(128, 128, 128, 0.5)!important;
}
.alert
{
background :rgba(0,128,128,0.5)!important;
}
tbody tr
{
background: rgba(92,92,92,0.5);
}
select
{
background: /*[[select]]*/!important;
}


/*Plain Text*/
p,
body,
div.tooltip,
input.text,
select,
textarea,
.post .spoiler.show dd,
#query,.panel-container h3

{
color: /*[[csa]]*/!important;
text-shadow: 1px 1px 4px /*[[elmsh]]*/ ;
}

/*Hyperlink Text*/
a:link,
a:visited,
.pagination,
.display h2,
#donations h2,
form.norm h2,
#content h1,
h1,
h2,
div.search_suggest_div ul,
input.button,
form.norm input
{
color: /*[[textcolor]]*/ !important ;
text-shadow: 1px 1px 4px /*[[elmsh]]*/ ;
}

/*Elements color*/
#head,
#sorting_controls,
#footer,
#donations .graph,
.display,
.display h2,
.display h3,
.image_block .meta,
.pagination li,
.notice,
form.norm h2,
form.norm h3,
form.norm,
form.norm input,
dl.spoiler.hide,
input.button,
div#content table,
#masstags_form input.text
{
background: /*[[csc]]*/!important;
}

/*Title bar, form color*/
#sidebar .display h2,
h3,
form.norm h2,
form.norm h3,
tfoot,
thead,
.display h2,
.display h3,
.title,
.post blockquote
{
background: /*[[csb]]*/!important;
}

/*readability*/
.image_block .meta,
input.text,
textarea
{
background: /*[[readability]]*/ !important;
}

/*Elements' background below are disabled by default to achieve transparency effect*/
p,
.display .title h2,
.display .title h3,
.display dl,
.odd,
.post .content,
.post .spoiler,
.post_foot,
.post .avatar,
#banner,
form.norm input,
#masstags_form,
.minitabs .tab > a
{
background:transparent !important;
}

.display dl
{
border-left: 10em solid transparent !important;
}

.thumbview .display
{
border-width: 0 0 0 1px;
}

div.search_suggest_div,
div.search_suggest_div ul,
div.search_suggest_div li.close,
div.tooltip
{
background:/*[[menucolor]]*/!important;
}

a.admin,
span.admin{
color:/*[[admin]]*/!important;
}
a.tagger,
span.tagger{
color:/*[[tagger]]*/!important;
}
a.moderator,
span.moderator{
color:/*[[mod]]*/!important;
}
a.reg_user
{
color:/*[[reg_user]]*/!important;
}
}

