/*

=================================================================================================================
fiaKaiera's - Ask PokéProfs Tumblr Theme
An overhaul on the aesthetic on Tumblr following the theme of ask-pokeprofs on Tumblr.

Gallery: http://fiakaiera.deviantart.com

How to use: (if not installing via userstyles.org)
1. Copy all of this code. (Ctrl+A -> Ctrl+C)
2. Write a new style in Stylish.
3. Paste the code. (Ctrl+V)
4. Save!
5. Make sure it's enabled.
6. Have fun!
=================================================================================================================

*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.tumblr.com") {
  /*----------------------------------------------------------------*/
  /* REMOVE FAKE CLASS                                              */
  /*----------------------------------------------------------------*/
  .fake{
    visibility: hidden;
    position: absolute;
    margin: 0;
  }
  
  /*[[fontInsert]]*/
  /*[[themeInsert]]*/

  .post-tab-switching, .post-forms-modal, .post-forms-glass, .ui_peepr_glass{
    background: rgba(17, 17, 17, 0.8) none repeat scroll 0% 0% !important;
  }
  
  .l-container{
    padding: 92px 0 0 !important;
  }

  .l-container.l-container--two-column-dashboard .right_column,
  .l-container.l-container--two-column .right_column{
    width: 229px;
  }

  .l-container .l-content{
    padding: 20px !important;
    width: 895px !important;
    margin: 0 auto;
  }
  
  #error_old_ie, #error_no_javascript, .l-container{
    width: 100%;
    padding: 10px;
  }
  
  .identity .right_column{
    background: #222 none repeat scroll 0% 0% !important;
    margin: 0px !important;
    padding: 10px 10px 20px !important;
    border-radius: 5px;
  }
  
  .identity-refresh .tab_notice {
    background-color: #FFDE43;
  }
  
  .tab_notice.tab-notice--outlined.inbox_notice_container.tab-notice--active{
    background-color: #C64E29;
  }
  
  .xkit-shorten-posts-embiggen{
    z-index: 1;
    background: rgba(230, 230, 230,0.9) none repeat scroll 0% 0%;
  }
  
  .identity .controls_section li.section_header{
    border-bottom: #FFDE43 2px solid;
  }
  
  .identity .controls_section li:hover:not(.section_header):not(.selected_blog){
    background-color: rgba(255, 222, 67,.2);
    border-radius: 5px;
  }
  
  .xtimestamp{
    margin: 0px 20px;
    position: absolute;
    top: 13px;
    right: 0;
  }

  .xkit-fan-timestamp{
    position: static;
    padding: 0 0 5px;
  }
  
  .post_full.fan_mail .post_controls{
    height: 32px;
  }
  
  .flag--dashboard-icon-revolution .post_controls .post_control.post-control-icon{
    width: 30px;
  }
  
  .flag--dashboard-icon-revolution .post_controls .post_control.reblog {
    margin-right: 5px;
  }

  .post_controls .post_controls_inner{
    padding: 10px 0;
  }
  
  .post_controls .post_control, .post_controls .post_control.reblog{
    margin: 0px 5px -5px 5px;
    width: 30px;
    height: 23px;
    padding: 0px 0px 0px 0px !important;
    background: #FFDE43;
    border-radius: 5px;
  }
  
  .post_full .post_footer{
    color: rgba(55,55,55,0.5);
  }
  
  .post_controls .post_control.no_icon{
    padding-top: 2px !important;
  }
  
  .post.new_post{
    margin: 0; padding:0;
  }
  
  .ui_avatar .frame.follower::before{
    background: #C64E29 none repeat scroll 0% 0%;
  }
  
  .pagination{
    padding: 0px !important;
    padding-left: 85px !important;
  }
  
  .l-container--two-column .pagination{
    padding: 10px !important;
  }
  
  .xkit-replyviewer::after{
    margin-top: -9px !important;
    margin-left: -10px !important;
  }
  
  .post_controls .post_control.queue_submission::before{
    margin-top: -12px;
    margin-left: -13px;
  }
  
  .post_controls .post_control.share::before,
  .post_controls .post_control.like::before,
  .post_controls .post_control.reblog::before,
  .post_controls .post_control.messaging::before,
  .post_controls .post_control.reply::before,
  .post_controls .post_control.edit::before,
  .post_controls .post_control.post_control_menu::before,
  .post_controls .post_control.message-controls::before{
    margin-top: -3px;
    margin-left: 3px;
  }
  
  .post_controls .post_control.no_icon, .post_controls .post_control.no_icon.show_label{
    width: 50px;
    text-align: center;
    padding-top: 0px !important;
    color: rgba(30,30,30,0.70);
  }
  
  .post_controls .post_control.post-control-icon{
    width: 31px;
  }
  
  .flag--dashboard-icon-revolution .post_controls .post_control::before,
  .post_controls .post_control::after{
    color: #000;
    opacity: 0.6;
  }
  
  .rapid-recs-container .rapid-recs{
    background-image: linear-gradient(to bottom, rgba(54, 54, 54, 0) 10%, #111 15%, #111 85%, rgba(54, 54, 54, 0) 90%);
    padding: 60px 0px 60px 0px;
    width: 200px;
    right: -220px;
  }
  
  .popover_menu_item{
    margin: 3px;
  }

  .ace_editor *{
    font-family: "Consolas" !important;
    font-size: 12px !important;
  }

  /*----------------------------------------------------------------*/
  /* SETTINGS PAGE                                                  */
  /*----------------------------------------------------------------*/
  
  .tumblr_settings .left_column{
    background: #fff !important;
  }
  
  /*----------------------------------------------------------------*/
  /* FOLLOWING PAGE                                                 */
  /*----------------------------------------------------------------*/
  
  #following{
    background: #fff !important;
  }
  
  #following #left_column{
    background: #fff !important;
  }
  
  #tabs{
    margin-bottom: 0 !important;
    background: #222;
    box-sizing: border-box;
    width: 100%;
    border-bottom: #FFDE43 3px solid;
    padding-left: 120px !important;
  }
  
  #tabs .tab, #tabs .tab.selected{
    padding: 5px;
    width: 200px !important;
    background: #333;
    border: 0 !important;
    border-radius: 5px 5px 0 0 !important;;
    margin: 0 5px;
    color: #ddd;
  }
  
  #tabs .tab.selected{
    color: #333;
    background: #FFDE43;
  }
  
  #following, #lookup, #staff_picks{
    padding: 15px 15px 15px;
    background: #333 !important;
  }
  
  #staff_picks{
    padding: 20px 0px 15px;
    background: #333 !important;
  }
  
  .chrome_nav{
    margin: 0;
    border: 0;
  }
  .chrome_nav .chrome.sidebar, .chrome_nav .chrome{
    border: 0;
    margin: 0;
    margin-bottom: 2px;
    background: #FFDE43;
    border-radius: 5px 0 0 5px;
  }
  
  .chrome_nav .chrome.sidebar:hover{
    color: #ccc;
  }
  
  .chrome_nav .chrome{
    background: #555;
  }
  
  /*----------------------------------------------------------------*/
  /* HELP PAGE                                                      */
  /*----------------------------------------------------------------*/
  #help_full{
    background: #eee;
    padding-bottom: 20px;
    border-radius: 0 0 10px 10px;
  }
  
  #help_footer{
    margin-bottom: 10px;
  }
  
  .responsive-help-docs.help-context #help_full .no_results, p.result{
    margin-top: 0;
  }
  
  .corp_page #left_column, .pages_actions_api_docs #left_column{
    background: #eee !important;
    padding: 10px;
    border-radius: 10px;
  }
  
  .doc_footer{
    width: 945px;
    padding: 0 0 15px;
  }
  
  .doc_footer .flags{
    float: left;
    background: #F6F6F6 repeat scroll center top;
    padding: 0px 50px 5px;
    text-align: center;
  }
  
  .doc_footer .flags a{
    white-space: nowrap;
  }
  
  .doc_footer .separator{
    margin-bottom: 5px;
  }
  
  .responsive-help-docs.help-context .l-container.l-container--full-width-gutterless .l-content,
  .responsive-help-docs.help-context .l-container.l-container--full-width .l-content {
    padding: 0px 0px 30px;
    border-radius: 15px;
  }
  
  .email_support{
    margin-top: 20px;
    margin-bottom: -30px;
    padding: 10px 0 10px;
    text-align: center;
    border-top: 0;
    background: #eee;
  }
  
  /*----------------------------------------------------------------*/
  /* ABOUT PAGE / OTHER PAGES                                       */
  /*----------------------------------------------------------------*/
  
  .rad_slideshow, .rad_slideshow .slide {
    width: 945px;
    border-radius: 15px;
    margin-bottom: 10px;
  }
  
  .rad_slideshow::after{
    height: 0;
  }
  
  .staff_posts{
    background: #f6f6f6;
    padding: 20px;
    margin: 0 auto;
    border-radius: 20px;
  }
  
  .corp_page .vertical_tab_set{
    background: #e0e0e0;
    border-radius: 5px 0 0 5px;
    border-bottom: 0;
  }
  
  .corp_page .vertical_tab_set a{
    border-top: 0;
  }
  
  .corp_page .vertical_tab{
    border-radius: 5px 0 0 5px;
  }
  
  .corp_page #right_column{
    margin-top: 0;
  }
  
  .recessed_container, #logo-page{
    background: #f6f6f6;
    border-radius: 20px;
  }
  
  #logo-page{
    padding: 10px 0px;
  }
  
  .business_page_v3 #content{
    display: inline-block;
    background: #f6f6f6 none repeat scroll 0% 0%;
  }
  
  .pages-footer-container::before{
    content: " ";
    position: absolute;
    background: #f6f6f6;
    left: 0;
    width: 100%;
    height:126px;
    z-index: -1;
    margin-top: -26px;
  }
  
  .buttons-page .button-faqs, .buttons-page .buttons-content{
    display: inline-block;
    background: #f6f6f6;
    width:100%;
    max-width: 100%;
    padding: 30px 0px;
    margin: -5px 0;
  }
  
  .buttons-page .button-faqs .faqs-image.right{
    margin-right: 0;
  }
  
  .buttons-page .button-faqs .faqs-image.left{
    margin-left: 0;
  }
  
  .buttons-page .button-faqs .faq{
    padding: 0 30px;
  }
  
  .corp_page .l-content .page_content, .meetups-introduction.section, .meetup-footer.section, .terms-of-service.section, .theme_garden_container{
    background: #f6fef6;
    padding: 20px;
    border-radius: 15px;
  }
  
  .press_list{
    padding: 20px 20px;
  }
  
  .jobs_page .pages-section, .upcoming-meetups{
    background: #f6f6f6;
  }
  
  #whitehat_form div{
    background: transparent !important;
  }
  
  .carousel{
    background-color: #C64E29;
  }
  
  .carousel_controls .previous::before, .carousel_controls .next::before{
    background: transparent linear-gradient(to right, #C64E29 0px, rgba(198, 78, 41, 0) 100%) repeat scroll 0% 0%;
  }
  /*----------------------------------------------------------------*/
  /* REMOVE BLUE                                                    */
  /*----------------------------------------------------------------*/
  .identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .right_column:after, .identity-refresh .radar .radar_footer .radar_avatar:before, .discover .blog_row .info:before, .discover .blog_row:hover .info:before {  
    border-color: transparent;
    background: transparent;
  }

  .identity-refresh .post_avatar .post_avatar_link, #toaster .toast .toast_border{
   background-color: transparent !important;
  }

  .dashboard_options_form, .tag_psa {
   border-color: transparent;
  }
  
  .compose-button, .compose-button:active, .compose-button:focus, .identity-refresh .search_results_container .tumblelog_mask_item {
   color: #fff;
  }

  .plus-follow-button, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container, .identity-refresh .tab_notice .tab_notice_value {
   color: #333;
   border-color: #333;
  }

  #dashboard_controls a, #dashboard_controls .control {
   color: #aaa;
   text-shadow: #333 1px 1px 1px;
  }

  .dashboard_options_form .dashboard_options_form_disclaimer, .dashboard_options_form .dashboard_options_form_disclaimer a, .post_full .post_avatar .queue .publish_info.time {
   color: #aaa;
  }

  #dashboard_controls a:hover, #dashboard_controls .control:hover, .dashboard_options_form, .tag_psa, .post_full .post_avatar .queue .publish_info.day {
   color: #ccc;
  }

  .glass.blue, #hello_glass.blue, #glass_overlay.blue, .identity-refresh .plexi.show.color, .post-forms-glass {
   background: rgba(51,51,51,.9);
  }
}
