/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* NOTE: On legacy browser versions might help replacing 'menupopup' with 'popup' on selectors */

@-moz-document regexp("^chrome://\\w+/content(?:/[\\w-]+)*.xul$"), url-prefix("about:") {

menupopup .box-inherit.scrollbox-innerbox {
  margin-bottom: 1px !important;  /* slightly increase this value if any unusual popup menu overflow happens */
}
panelview .panel-subview-body {
  padding-bottom: 2px !important; 
}

/*[[grade]]*/

}
