css:
@-moz-document domain("cryptowat.ch") {
header#header {
    visibility: hidden;
    display: none;
}
main#marketview {
    top: 0px !important;
}
#marketview {
    bottom: 0px !important;
}
#viewport #chart-navigation {
    height: 15px;
}
.footer-dropup .option,
.header-dropdown .option {
    font-size: 9px;
    padding: 2px 2px;
}
.footer-dropup .option:first-child,
.header-dropdown .option:first-child {
    padding-top: 2px;
}
#viewport #chart-navigation .dropdown-target,
#viewport #chart-navigation .fake-dropdown-target {
    padding: 2px 2px!important;
    font-size: 9px!important;
}
#bookmark-current {
    display: none !important;
}
#external-exchange-link {
    display: none !important;
}
#chart-nav-chart-style {
    display: none !important;
}
#chart-nav-overlays {
    display: none !important;
}
#chart-nav-drawing-tools {
    display: none !important;
}
#take-snapshot {
    display: none !important;
}
#exit-fullscreen,
#request-fullscreen {
    cursor: pointer;
    z-index: 910;
    padding: 2px 2px;
    font-size: 10px;
}
#axis-mode {
    width: 60px;
    padding: 0 2px;
    font-size: 9px;
    line-height: 15px;
}
#tools {
    display: none !important;
}
.notification-list {
    display: none !important;
}
#sidebar__col3 {
    display: none;
}
#sidebar__col2 {
    width: 180px;
}
.sidebar__col__cell.sidebar__col__cell--header {
    min-height: 12px;
}
#agg-controls > div {
    padding: 2px 2px;
    line-height: 12px;
}
#agg-controls > div .agg-controls__display {
    padding-right: 2px;
}
.agg-controls__display {
    display: none !important;
}
#sidebar__col1 {
    display: none !important;
}
.sidebar__col-collapse {
    display: none !important;
}
.sidebar__col__cell.sidebar__col__cell--fixed-height {
    height: 70%;
}
#price-ticker {
    font-size: 11px;
    min-height: 10px;
    line-height: 12px;
    font-family: verdana;
    color: #00ffff !important;
}
#current-time {
    display: none !important;
}
.order-list-entry li .amount-component {
    width: 65px !important;
}
ul.feed li {
    font-size: 10px;
}
footer {
    display: none !important;
}
}
