@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plus.google.com") {
  body#yDmH0d {
    background-color: #cde;
    background-image: url('/*[[BACKGROUND_IMAGE]]*/') !important;
    background-size: cover;
    background-attachment: fixed;
    background-position: center;
  }

  .Jvazdb, .JXv70c {
    background: transparent;
  }
  
  body#yDmH0d > trans-layer > div {
    background-color: transparent !important;
  }
  
  /* Post */
  
  .Ihwked, .LHyahc {
    background-color: rgba(255, 255, 255, .8);
    border: none;
    box-shadow: 0px 2px 2px rgba(80, 80, 80,.75) !important;
  }
  
  .E4TZmd {
    background-color: transparent;
  }
  
  .fu5e3b.qhIQqf:after {
    background: transparent;
  }

  /* Post Reference */
  
  .B5gIxb, .bTEgD, .F7tj9b, .gdAenb {
    background-color: transparent;
  }
  
  .X2MVjb, .UkMYHf, .HroxVe {
    border-color: rgba(150, 150, 180, .8);
  }
  
  .pQcgke {
    border: none;
    box-shadow: 0px 2px 2px rgba(80,80,80,.75) !important;
  }

  .YVtJp {
    border: none;
  }

  .tjHUud {
    background-color: rgba(128,192,224,.2);
    border-top: 1px solid #ccc;
    border-bottom: 1px solid #ccc;
    padding-bottom: 2px;
  }
  
  /* Post Comments */
  
  .LZOeXd, .pQGRZe, .cYxVC {
    background-color: rgba(120,140,210,.25);
    border-top: 1px solid rgba(150, 150, 180, .8);
  }
  
  .cjGgHb .M24KRd:hover {
    background-color: rgba(255, 255, 255, .5);
  }
  
  /* Menu */
  
  .OFyC1e {
    background-color: rgba(255, 255, 255, .3);
  }
  
  .cjGgHb .M9kDrd:hover, .M9kDrd:focus {
    background-color: rgba(255, 255, 255, .5);
  }

  .M9kDrd .DCYkOe {
    fill: #ccc;
  }
}
