css:
@-moz-document domain(www.creepypastafromthecrypt.com) {
body {
background: url(http://imgur.com/s0VHPxY.jpg);
background-color: #000000;
scrollbar-face-color: #111111;
scrollbar-highlight-color: #0E0E0E;
scrollbar-shadow-color: #111111;
scrollbar-3dlight-color: #1a1a1a;
scrollbar-arrow-color: #666666;
scrollbar-track-color: #000000;
scrollbar-darkshadow-color: #0a0a0a;
background-image: url("/*[[Colors]]*/");
background-attachment: fixed;
}

body, a, a:hover {
cursor: url("/*[[Curseur]]*/"), progress;
}
  
font,th,td,p { font-family: Verdana, Arial, Helvetica, sans-serif }
a:link,a:active,a:visited { color : #CCCCCC; }
a:hover { text-decoration: underline; color : #CCCCCC; }
hr	{ height: 0px; border: solid #1a1a1a 0px; border-top-width: 1px;}
.forumline	{ background-color: #1a1a1a; border: 2px #0a0a0a solid; }
td.row1	{ background-color: /*[[hexa1]]*/; }
td.row2	{ background-color: /*[[hexa2]]*/; }
td.row3	{ background-color: #5F6B77; }
td.rowpic {
background-color: #0E0E0E;
background-image: url(http://i.imgur.com/kuy0aBI.jpg);
background-repeat: repeat-y;
}
th	{
color: #CCCCCC; font-size: 11px; font-weight : bold;
background-color: #666666; height: 25px;
background-image: url(http://imgur.com/qmTlScu.jpg);
}
td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom {
background-image: url(http://imgur.com/GzAptFE.jpg);
background-color:#1a1a1a; border: #1a1a1a; border-style: solid; height: 28px;
}
td.cat,td.catHead,td.catBottom {
height: 29px;
border-width: 0px 0px 0px 0px;
}
th.thHead,th.thSides,th.thTop,th.thLeft,th.thRight,th.thBottom,th.thCornerL,th.thCornerR {
font-weight: bold; border: #0E0E0E; border-style: solid; height: 28px;
}
td.row3Right,td.spaceRow {
background-color: #1a1a1a; border: #1a1a1a; border-style: solid;
}
th.thHead,td.catHead { font-size: 12px; border-width: 1px 1px 0px 1px; }
th.thSides,td.catSides,td.spaceRow { border-width: 0px 1px 0px 1px; }
th.thRight,td.catRight,td.row3Right { border-width: 0px 1px 0px 0px; }
th.thLeft,td.catLeft { border-width: 0px 0px 0px 1px; }
th.thBottom,td.catBottom { border-width: 0px 1px 1px 1px; }
th.thTop { border-width: 1px 0px 0px 0px; }
th.thCornerL { border-width: 1px 0px 0px 1px; }
th.thCornerR { border-width: 1px 1px 0px 0px; }
.maintitle	{
font-weight: bold; font-size: 22px; font-family: "Trebuchet MS",Verdana, Arial, Helvetica, sans-serif;
text-decoration: none; line-height : 120%; color : #CCCCCC;
}
.gen { font-size : 12px; }
.genmed { font-size : 11px; }
.gensmall { font-size : 10px; }
.gen,.genmed,.gensmall { color : #AAAAAA; }
a.gen,a.genmed,a.gensmall { color: #666666; text-decoration: none; }
a.gen:hover,a.genmed:hover,a.gensmall:hover	{ color: #888888; text-decoration: underline; }
.mainmenu { font-size : 11px; color : #AAAAAA }
a.mainmenu { text-decoration: none; color : #666666; }
a.mainmenu:hover{ text-decoration: underline; color : #888888; }
.cattitle { font-weight: bold; font-size: 12px ; letter-spacing: 1px; color : #CCCCCC}
a.cattitle { text-decoration: none; color : #CCCCCC; }
a.cattitle:hover{ text-decoration: underline; }
.forumlink { font-weight: bold; font-size: 12px; color : #CCCCCC; }
a.forumlink { text-decoration: none; color : #CCCCCC; }
a.forumlink:hover{ text-decoration: underline; color : #888888; }
.nav { font-weight: bold; font-size: 11px; color : #AAAAAA;}
a.nav { text-decoration: none; color : #CCCCCC; }
a.nav:hover { text-decoration: underline; }
.topictitle,h1,h2	{ font-weight: bold; font-size: 11px; color : #CCCCCC; }
a.topictitle:link { text-decoration: none; color : #CCCCCC; }
a.topictitle:visited { text-decoration: none; color : #666666; }
a.topictitle:hover	{ text-decoration: underline; color : #888888; }
.name { font-size : 11px; color : #AAAAAA;}
.postdetails { font-size : 10px; color : #AAAAAA; }
.postbody { font-size : 12px; line-height: 18px; color: /*[[texte]]*/ }
a.postlink:link	{ text-decoration: none; color : #666666 }
a.postlink:visited { text-decoration: none; color : #666666; }
a.postlink:hover { text-decoration: underline; color : #888888}
.code {
font-family: Courier, \'Courier New\', sans-serif; font-size: 11px; color: #8b90c1;
background-color: #1a1a1a; border: #1a1a1a; border-style: solid;
border-left-width: 1px; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px
}
.quote {
font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 11px; color: #666666; line-height: 125%;
background-color: #1a1a1a; border: #1a1a1a; border-style: solid;
border-left-width: 1px; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px
}
.copyright { font-size: 10px; font-family: Verdana, Arial, Helvetica, sans-serif; color: #666666; letter-spacing: -1px;}
a.copyright { color: #666666; text-decoration: none;}
a.copyright:hover { color: #AAAAAA; text-decoration: underline;}
input,textarea, select {
color : #AAAAAA;
font: normal 11px Verdana, Arial, Helvetica, sans-serif;
border-color : #AAAAAA;
}
input.post, textarea.post, select {
background-color : #1a1a1a;
}
input { text-indent : 2px; }
input.button {
background-color : #1a1a1a;
color : #AAAAAA;
font-size: 11px; font-family: Verdana, Arial, Helvetica, sans-serif;
}
input.mainoption {
background-color : #1a1a1a;
font-weight : bold;
}
input.liteoption {
background-color : #1a1a1a;
font-weight : normal;
}
.helpline { background-color: #111111; border-style: none; }

  }
