css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tutorialspoint.com") {  
	
	:root{
		--body-bg-color: #282828;
		--main-bg-color: #484848;
		--main-text-color: #E8E8E8;
		--code-bg-color: #B8B8B8;
	}

	header, section.featured.map{
		background: var(--main-bg-color);
		border-color: var(--main-bg-color);
	}

	body{
		background: var(--body-bg-color);	
		color: var(--main-text-color);
	}

	body, body.sticky-menu-active header{	
		border-color: var(--body-bg-color)
	}

	.middle-col {
		background: var(--main-bg-color);
		border-color: var(--main-bg-color);
	}

	ul.list li{
			color: var(--main-text-color);
	}

	div.featured-box {
		color: var(--main-text-color);
		background: var(--main-bg-color);
		border-color: var(--main-bg-color);
	}

	ul.menu li a{
		color:var(--main-text-color) !important;
	}

	ul.menu li a:hover{
		color:var(--body-bg-color) !important;
	}

	td a, p a, .launcher{
		color: #B9B9B9 !important;
	}

	div.content p {
		color: var(--main-text-color);
	}

	ul.nav-list.primary > li a {
		color: #888888;
	}

	div.simple-ad{
		border-color: transparent;
	}

	h1, h2, h3, h4 {
		text-shadow: 0px 0px 0px;
		color: var(--main-text-color) !important;
	}

	.content h3{
		color: var(--main-text-color);
		text-decoration: underline;
	}

	span.pln, pre.result, pre.prettyprint, pre.prettyprint.tryit {
		color:black;
		background-color: var(--code-bg-color) !important;
		border-color: var(--code-bg-color);
	}

	th{
		color:var(--body-bg-color);
	}

	tr{
		background-color: var(--main-bg-color);
	}

	blockquote{
		background-color: #363636;
		color: var(--main-text-color);
		border-color: var(--code-bg-color);
	}

}
