css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.notes.na.collabserv.com") {
  
  /* Top banner */

.ics-scbanner {
    background-color: /*[[mColor]]*/;
}

  .seq-msg-row.importance:before {
    background-color: /*[[mColor]]*/;
  }                 


  .seq-msg-row.importance .avatar {
    border: 2px solid /*[[mColor]]*/;    
  }

  .outgoing-tray {
    background-color: /*[[mColor]]*/; 
  }  
  
  .eventPopup > .meeting dd.subject, .eventPopup > .alldayevent dd.subject, .eventPopup > .anniversary dd.subject, .eventPopup > .meeting a, .eventPopup > .alldayevent a, .eventPopup > .anniversary a {
    color: /*[[mColor]]*/;
} 
  
  .eventPopup > .meeting::before, .eventPopup > .alldayevent::before, .eventPopup > .anniversary::before {
    background: /*[[mColor]]*/;
}

.seq-msg-row > .avatar {
   
    border: 1px solid /*[[mColor]]*/;
  
}
  
.seq-msg-row > .avatar .initials {
  
     color: /*[[mColor]]*/;
}

  .seq-msg-row.selected {
    background-color: /*[[thrColor]]*/;
}
  
  .seq-window.drafts-open .show-drafts:hover, .seq-window.drafts-open .show-drafts:focus {
    border-color: /*[[thrColor]]*/;
}
  
  .seq-window.sent-open .show-sent:hover, .seq-window.sent-open .show-sent:focus {
    border-color: /*[[thrColor]]*/;
}
  
  .seq-window.sent-open .show-sent {
    border-bottom: 3px solid /*[[thrColor]]*/;
}
  .seq-window.drafts-open .show-drafts {
    border-bottom: 3px solid /*[[thrColor]]*/;
}
 

.set-base.selectedSet {
   
    border-color: /*[[secColor]]*/;

}
 
  .seq-window.tray-open .outgoing-button {
    background-color: /*[[mColor]]*/;  
}
  
  .seq-window .compose-button {
    color: #FFF;
    background-color: /*[[secColor]]*/;   
}
  
  .seq-window .outgoing-button {
    color: #FFF;
    background-color: /*[[secColor]]*/;
}
  
  .seq-msg-row.focused:before, .seq-msg-row.focused::before {
    border-right-color: /*[[secColor]]*/;
}

.seq-msg-row.focused:before, .seq-msg-row:focus::before {
    border-right: 7px solid /*[[secColor]]*/;
    border-right-width: 7px;
    border-right-style: solid;
    border-right-color: /*[[secColor]]*/;
}

  .calendarDate .calendarToday {
    color: /*[[mColor]]*/;
}
  
  .calendarDate .calendarToday {
    color: /*[[mColor]]*/;
}


  
  /* Next meeting subject*/

  #nextMeeting .nextMeetingSubject {
    color: /*[[secColor]]*/;
  }

  

  /* Next meeting event bar */

  #eventBar .meeting{
    background-color: /*[[secColor]]*/;
  }


.outgoing-view:focus .outgoing-msg-info, .outgoing-view:hover .outgoing-msg-info {
    border-color: /*[[secColor]]*/;
    background-color: /*[[mColor]]*/;
}
  
  .outgoing-msg-info { 
    border-bottom: 1px solid /*[[thrColor]]*/;
    border-top: 1px solid /*[[thrColor]]*/;
}
  
 /* Next meeting subject*/

  #nextMeeting .nextMeetingSubject {
    color: /*[[mColor]]*/; 
  }

  

  /* Next meeting event bar */

  #eventBar .meeting{
    background-color: /*[[mColor]]*/; 
  }

  

}
