css:
@-moz-document domain("web.telegram.org"){
.page_wrap{background-image:url(/*[[BG]]*/)!important;background-size:cover!important;}
/*[ng-show="state.loaded"]{background-image:url(https://lh6.googleusercontent.com/-sbl0Uy3mo7o/VT41TYowMcI/AAAAAAAACVU/bb9_EbrAqgo/w703-h563-no/tumblr_nf9ypizt5Y1qgn8oho1_12801.jpg)!important;background-size:cover!important;border:none!important;}*/

.im_history_col .nano > .nano-pane > .nano-slider,.contacts_modal_col .nano > .nano-pane > .nano-slider,.sessions_modal_col .nano > .nano-pane > .nano-slider,.stickerset_modal_col .nano > .nano-pane > .nano-slider,.im_dialogs_modal_col .nano > .nano-pane > .nano-slider,.composer_emoji_tooltip .nano > .nano-pane > .nano-slider, .composer_dropdown_wrap .nano > .nano-pane > .nano-slider{background:rgb(40, 108, 111);-webkit-border-radius:5px;-moz-border-radius:5px;border-radius:5px;}
.has-scrollbar .nano-pane .nano-slider{margin-left:0.5px!important;}
.tg_head_btn:hover,.tg_head_btn,.tg_head_split,.nano-slider{background:#215456;}
.im_dialogs_scrollable_wrap a.im_dialog:hover,.im_dialogs_scrollable_wrap a.im_dialog_selected,.im_history_select_active .im_message_outer_wrap:hover,.im_message_outer_wrap:hover,.im_history_message_wrap .im_message_outer_wrap:hover,.im_dialogs_scrollable_wrap .active a.im_dialog{background-color:#181818!important;}
.im_message_selected .im_message_outer_wrap,.im_history_message_wrap.im_message_selected .im_message_outer_wrap:hover{background:#060606!important;}
.im_dialogs_scrollable_wrap a.im_dialog,.im_page_wrap{background-color:#242424!important;}
.im_dialog_peer{color:#999;}
.im_dialogs_col_wrap{border-right:2px solid #2c2c2c;}
.im_page_wrap{border-left:1px solid #2c2c2c;-webkit-box-shadow:none;border-right:1px solid #2c2c2c;border-bottom:1px solid #2c2c2c;}
.im_message_unread_split{background:#6F6F6F;color:#D2D2D2;}
.im_history_select_active .im_message_outer_wrap:hover,.im_message_selected .im_message_outer_wrap{background:#F2F6FA;}
.im_message_reply_wrap,.im_message_reply{color:#727272;}
.im_message_text,.im_message_webpage_description{color:rgb(164,164,164);}
.im_dialogs_search_field{background-color:#505050;border:1px solid #2c2c2c;border-radius:5px;}
.im_dialogs_search_field:active,.im_dialogs_search_field:focus{background-color:#454545;}
.form-control.no_outline:focus{border:1px solid #2c2c2c;}
.im_edit_panel_border{border-bottom:1px solid #2c2c2c;}
.dropdown.open .tg_head_btn,.tg_head_btn:hover,.tg_head_peer_dropdown .dropdown-menu,.tg_head_logo_dropdown .dropdown-menu{background:#1B4042;border:none;}
.tg_head_logo_dropdown .dropdown-menu > li > a:hover,.tg_head_peer_dropdown .dropdown-menu > li > a:hover{background:#237579;}
.tg_head_peer_dropdown .dropdown-menu > li > a,.tg_head_logo_dropdown .dropdown-menu > li > a{color:#D2D2D2;}
.has-scrollbar .nano-slider{background:rgb(33,84,86);margin:0 -2px!important;width:8px;-webkit-border-radius:5px;-moz-border-radius:5px;border-radius:5px;}
.btn-primary.disabled,.btn-primary[disabled]{background:#304546;}
.btn-md-primary,.btn-md-primary:focus,.btn-md-primary:hover{color:#669498;}
.btn-md:hover,.btn-primary{background:#3D7577;color:#DCDCDC!important;}
.btn-primary.active,.btn-primary:active,.btn-primary:focus,.btn-primary:hover,.open .btn-primary.dropdown-toggle{background-color:#215456;}
.composer_rich_textarea,.composer_textarea{box-shadow:0px 1px 0px 0px rgba(107,107,107,0.65);color:#727272;}
.im_dialog_badge_muted{background:#1ABEC5;}

.composer_emoji_tooltip_content_wrap{background:#363636;}
.composer_emoji_tooltip_categories,.icon-tooltip-tail,.composer_emoji_tooltip_tabs{background:#656565;}
.composer_emoji_tooltip{border:none;}
.icon-tooltip-tail{border-color:#363636;}

/* test */
.im_dialog_photo, .im_message_contact_photo, .im_message_from_photo, .im_message_fwd_photo, .im_panel_own_photo, .im_panel_peer_photo {
border-radius: 3px;
}
}
