css:
@-moz-document
	url-prefix("https://userstyles.org/styles/"),
	url-prefix("https://userstyles.org/users/") {

  .no-screenshots {
    background: url(/*[[no-screenshot]]*/) center center/contain no-repeat #eee;
    padding: 80px 0 20px;
    color: /*[[fg-color]]*/;
    text-shadow: 2px 2px 3px /*[[bg-color]]*/, -2px -2px 3px /*[[bg-color]]*/, -2px -2px 3px /*[[bg-color]]*/, 2px -2px 3px /*[[bg-color]]*/;
    margin-top: 0 !important;
/*    animation: cylon 10s linear infinite;*/
/*    animation: cheshire 5s linear alternate infinite;*/
    animation: /*[[animation]]*/ 5.9s linear infinite;
  }

  /* mix up the tempo */
  article:nth-child(3n+1) .no-screenshots {animation-delay: 0s; animation-duration: 6s;}
  article:nth-child(3n+2) .no-screenshots {animation-delay: 2s; animation-duration: 5.7s;}
  article:nth-child(3n+3) .no-screenshots {animation-delay: 4s; animation-duration: 5.3s;}

  @keyframes cylon {
    0% {background-position: 50% 50%; transform: rotateX(0deg);}
    25% {background-position: 0% 50%;; transform: rotateX(90deg);}
    75% {background-position: 100% 50%;; transform: rotateX(270deg);}
    100% {background-position: 50% 50%;; transform: rotateX(360deg);}
  }
  @keyframes cheshire {
    0%, 40% {opacity: 1}
    90%, 100% {opacity: .2}
  }
  @keyframes jiggle {
    0%, 7% {background-position: 50% 50%;}
    1%, 3%, 5% {background-position: 48% 50%;}
    2%, 4%, 6% {background-position: 52% 50%;}
  }
}
