css:
@-moz-document url-prefix("https://web.tweetdeck.com/") {
.column {
    width: 32.7%!important
}
.tweet-avatar {
    background: transparent;
    -moz-border-radius: 24px;
    border-radius: 24px;
    -webkit-border-radius: 24px;
}
.activity-header .avatar {
    margin-top: -4px;
    margin-left: 6px;
    -moz-border-radius: 24px;
    border-radius: 24px;
    -webkit-border-radius: 24px;
}
.hearty .icon-favorite:before {
  content: "";
}

.hearty .is-favorite .icon-favorite-toggle {
  color: #FAB41E;
}

.hearty .icon-favorite-color {
    color: #FAB41E;
}

.hearty .sprite {
  background-image: url("https://ton.twimg.com/tweetdeck-web/web/assets/global/backgrounds/td_sprite_sheet_2013_@1x.e0708304e2.png");
}
.tweet-message .l-table {
 display: none !important;
}

.tweet-message .tweet-avatar {
 width: 24px;
 height: 24px;
}

.tweet-message .account-inline {
 line-height: 1.2em;
}

.tweet-message .tweet-timestamp {
 padding-top: 5px;
}

.is-unread .tweet-message .l-table {
 display: table !important;
 vertical-align: middle;
}

.tweet-message .conversation-indicator {
 display: none;
}

.tweet-message .nbfc {
 margin-left: -25px;
 line-height: 1.6em;
}

.tweet-message .tweet-text {
 margin-left: -24px;
}
}

