css:
@-moz-document domain("douban.com") {
/* 左栏 */
.grid-16-8>.article {
    font-size: 14px !important;
	width:620px !important;    
    padding-right:0 !important;
}
/* 长评正文 */
.review-text, #link-report>.review-content{
    font-size: 16px !important;
    line-height: 2em !important;
}
.comment-item {
    font-size: 15px !important;
}
#content > div > div.article > div > div.main > div.middle {
	margin-left:0 !important;
}
/*  图片float影响下方文字 */
#link-report .ll{
    float:none !important;
}
/* 问答 */
.answer > div.main {
	font-size: 14px !important;
}

}

@-moz-document regexp("https://movie\\.douban\\.com/subject/.*/discussion/.*") {
/* 讨论区主题 */
#link-report {
    font-size: 16px !important;
    line-height: 2em !important;
}
}
