@-moz-document regexp("https?://www.crunchyroll.(.*)/(?!user).*") {
/* ↓----------------------[ font ]----------------------↓ */

@font-face {
    font-family: "MavenPro-Crunchyroll_Edit";
    src: url('https://dl.dropboxusercontent.com/s/id1bvs7rtxafic4/MavenPro-Crunchyroll_Edit.woff?dl=0') format('woff');
    src: url('https://dl.dropboxusercontent.com/s/q5x3403im5qrv1s/MavenPro-Crunchyroll_Edit.ttf?dl=0') format('truetype'),
         url('https://dl.dropboxusercontent.com/s/lmkm2ftchkmqeqd/MavenPro-Crunchyroll_Edit.svg?dl=0') format('svg'),
         url('https://dl.dropboxusercontent.com/s/1vs2263hlre3r5e/MavenPro-Crunchyroll_Edit.otf?dl=0') format('opentype');
    font-weight: normal;
    font-style: normal;
    text-rendering: geometricPrecision!IMPORTANT;
    -webkit-font-smoothing: antialiased!IMPORTANT;
    -moz-osx-font-smoothing: grayscale!IMPORTANT;}

/* ↑----------------------[ font ]----------------------↑ */
/* ↓----------------------[ logo ]----------------------↓ */
	/* ----↓ for if your personal title is too long ↓---- */

/* ---[ first one moves your title left ]--- *
.header-logo, #logo_beta a::after {margin-left: -92px!IMPORTANT;}
.header-menubar {margin-left: 80px!IMPORTANT;}
/* ---[ second one moves your menu links right ]--- */

	/* ----↑ for if your personal title is too long ↑---- */
	/* ----↓ text ↓---- */

#logo_beta > a::before {
    font-family: "MavenPro-Crunchyroll_Edit"!IMPORTANT;
    content: '/*[[txt]]*/'!IMPORTANT;
    color: /*[[color]]*/!IMPORTANT;
    font-weight: 900!IMPORTANT;
    position: absolute;
    padding-left: 1.4em;
    top: 16.1pt;
    font-size: /*[[browser]]*/!IMPORTANT;}

	/* ----↑ logo ↑---- */
	/* ----↓ do not remove ↓---- */

#logo_beta > a > svg > use {display: none!IMPORTANT;}

	/* ----↑ do not remove ↑---- */
/* ↑----------------------[ logo ]----------------------↑ */}
